#!/usr/bin/env node

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

function buildForVercel() {
  console.log('üèóÔ∏è  Building for Vercel deployment...');

  try {
    // Copy assets first
    console.log('üìÅ Copying assets...');
    try {
      execSync('node copy-assets.js', { stdio: 'inherit' });
    } catch (error) {
      console.log('‚ö†Ô∏è  Asset copying failed, continuing...');
    }

    // Build the frontend
    console.log('üì¶ Building frontend...');
    execSync('npx vite build', { stdio: 'inherit' });

    // Build the backend (optional for Vercel)
    console.log('‚öôÔ∏è  Building backend...');
    try {
      execSync('npx esbuild server/index.ts --platform=node --packages=external --bundle --format=esm --outdir=dist', {
        stdio: 'inherit'
      });
    } catch (error) {
      console.log('‚ö†Ô∏è  Backend build failed, continuing...');
    }

    // Build the serverless function (optional for Vercel)
    console.log('üîß Building serverless function...');
    try {
      execSync('npx esbuild server/serverless.ts --platform=node --packages=external --bundle --format=esm --outdir=dist', {
        stdio: 'inherit'
      });
    } catch (error) {
      console.log('‚ö†Ô∏è  Serverless build failed, continuing...');
    }

    console.log('‚úÖ Build completed successfully!');
    console.log('');
    console.log('üìã Vercel Deployment Status:');
    console.log('  1. ‚úÖ Frontend built');
    console.log('  2. ‚úÖ Assets processed');
    console.log('  3. ‚ö†Ô∏è  Configure environment variables in Vercel');
    console.log('');
    console.log('üîó See VERCEL_DEPLOYMENT_GUIDE.md for setup instructions');

  } catch (error) {
    console.error('‚ùå Build failed:', error.message);
    process.exit(1);
  }
}

buildForVercel();

