migrate-db.ts.content:
import { db } from "./server/db";
import { sql } from "drizzle-orm";

async function migrateDatabase() {
  try {
    console.log('üîÑ Starting database migration...');

    // Check if method column exists
    const tableInfo = await db.all(sql`PRAGMA table_info(transactions)`);
    const hasMethodColumn = tableInfo.some((col: any) => col.name === 'method');

    if (!hasMethodColumn) {
      console.log('‚ûï Adding method column to transactions table...');
      await db.run(sql`ALTER TABLE transactions ADD COLUMN method TEXT`);
      console.log('‚úÖ Method column added successfully');
    } else {
      console.log('‚úÖ Method column already exists');
    }

    // Check if currency column exists
    const hasCurrencyColumn = tableInfo.some((col: any) => col.name === 'currency');

    if (!hasCurrencyColumn) {
      console.log('‚ûï Adding currency column to transactions table...');
      await db.run(sql`ALTER TABLE transactions ADD COLUMN currency TEXT`);
      console.log('‚úÖ Currency column added successfully');
    } else {
      console.log('‚úÖ Currency column already exists');
    }

    // Check if network_fee column exists
    const hasNetworkFeeColumn = tableInfo.some((col: any) => col.name === 'network_fee');

    if (!hasNetworkFeeColumn) {
      console.log('‚ûï Adding network_fee column to transactions table...');
      await db.run(sql`ALTER TABLE transactions ADD COLUMN network_fee TEXT`);
      console.log('‚úÖ Network_fee column added successfully');
    } else {
      console.log('‚úÖ Network_fee column already exists');
    }

    // Check if metadata column exists
    const hasMetadataColumn = tableInfo.some((col: any) => col.name === 'metadata');

    if (!hasMetadataColumn) {
      console.log('‚ûï Adding metadata column to transactions table...');
      await db.run(sql`ALTER TABLE transactions ADD COLUMN metadata TEXT`);
      console.log('‚úÖ Metadata column added successfully');
    } else {
      console.log('‚úÖ Metadata column already exists');
    }

    console.log('üéâ Database migration completed successfully!');

    // Verify the updated schema
    console.log('\nüìã Updated transactions table structure:');
    const updatedTableInfo = await db.all(sql`PRAGMA table_info(transactions)`);
    console.table(updatedTableInfo);

  } catch (error) {
    console.error('‚ùå Error during migration:', error);
  }
}

migrateDatabase();

reset-balance.ts.content:
import { db } from "./server/db";
import { balances, users } from "./shared/schema-sqlite";
import { eq, and } from "drizzle-orm";

async function resetUserBalance() {
  try {
    console.log('üîÑ Resetting user balance to 0...');

    // Find the trader1 user
    const [user] = await db.select().from(users).where(eq(users.username, 'trader1')).limit(1);

    if (!user) {
      console.log('‚ùå User trader1 not found');
      return;
    }

    console.log('üë§ Found user:', user.username);

    // Update USDT balance to 0
    const result = await db
      .update(balances)
      .set({ 
        available: '0.00',
        locked: '0.00',
        updatedAt: new Date()
      })
      .where(and(
        eq(balances.userId, user.id),
        eq(balances.symbol, 'USDT')
      ))
      .returning();

    if (result.length > 0) {
      console.log('‚úÖ Successfully reset USDT balance to 0.00');
      console.log('üí∞ New balance:', result[0]);
    } else {
      console.log('‚ùå No USDT balance found to update');
    }

    // Also reset BTC balance to 0
    const btcResult = await db
      .update(balances)
      .set({ 
        available: '0.00',
        locked: '0.00',
        updatedAt: new Date()
      })
      .where(and(
        eq(balances.userId, user.id),
        eq(balances.symbol, 'BTC')
      ))
      .returning();

    if (btcResult.length > 0) {
      console.log('‚úÖ Successfully reset BTC balance to 0.00');
    }

    console.log('üéâ Balance reset complete! User now starts with 0 balance.');

  } catch (error) {
    console.error('‚ùå Error resetting balance:', error);
  }
}

resetUserBalance();

