app.put('/api/admin/controls/:id', (req, res) => {
  console.log('üîÑ Update control request received for:', req.params.id, req.body);
  const { controlType, isActive } = req.body;
  const controlId = req.params.id;

  // Find and update the control in memory
  const controlIndex = demoControls.findIndex(control => control.id === controlId);

  if (controlIndex === -1) {
    return res.status(404).json({ message: 'Control not found' });
  }

  // Update the control
  if (controlType !== undefined) {
    demoControls[controlIndex].controlType = controlType;
  }
  if (isActive !== undefined) {
    demoControls[controlIndex].isActive = isActive;
  }
  demoControls[controlIndex].updatedAt = new Date();

  console.log('‚úÖ Control updated:', demoControls[controlIndex]);
  res.json(demoControls[controlIndex]);
});

