  const controlId = req.params.id;

  // Find and remove the control from memory
  const controlIndex = demoControls.findIndex(control => control.id === controlId);

  if (controlIndex === -1) {
    return res.status(404).json({ message: 'Control not found' });
  }

  // Remove the control
  const deletedControl = demoControls.splice(controlIndex, 1)[0];
  console.log('üóëÔ∏è Control deleted:', deletedControl);

  res.json({ message: 'Control deleted successfully', id: controlId });
});

