  } else {
    res.status(404).send(`
      <html>
        <body>
          <h1>CryptoTradeX Server</h1>
          <p>Server is running but static files not found.</p>
          <p>Please run: <code>npm run build</code></p>
          <p>Looking for files in: ${distPath}</p>
        </body>
      </html>
    `);
  }
});

// Error handling
app.use((err, req, res, next) => {
  console.error('‚ùå Server error:', err);
  res.status(500).json({ error: 'Internal server error' });
});

// Start server
app.listen(port, '127.0.0.1', () => {
  console.log('üéâ Server started successfully!');
  console.log(`üåê Server running on: http://127.0.0.1:${port}`);
  console.log(`üîó Try these URLs:`);
  console.log(`   - http://127.0.0.1:${port}/`);
  console.log(`   - http://127.0.0.1:${port}/api/health`);
  console.log(`   - http://127.0.0.1:${port}/api/test`);
}).on('error', (err) => {
  console.error('‚ùå Failed to start server:', err);
  if (err.code === 'EADDRINUSE') {
    console.log('üîÑ Port 5000 is already in use. Trying to kill existing process...');
  }
});

start-server.js.content:
// Wrapper script to start the server with better error handling
import { spawn } from 'child_process';
import path from 'path';

console.log('Starting server...');
console.log('Current directory:', process.cwd());
console.log('Node version:', process.version);

// Set environment variables
process.env.NODE_ENV = 'production';
process.env.PORT = '5000';

const serverPath = path.resolve(process.cwd(), 'dist', 'index.js');
console.log('Server path:', serverPath);

const server = spawn('node', [serverPath], {
  stdio: 'inherit',
  env: process.env
});

server.on('error', (error) => {
  console.error('Failed to start server:', error);
});

server.on('exit', (code, signal) => {
  console.log(`Server exited with code ${code} and signal ${signal}`);
});

console.log('Server process started with PID:', server.pid);

