// Simplified auth endpoint for Vercel
import type { VercelRequest, VercelResponse } from '@vercel/node';

// Simple in-memory session store for demo (in production, use Redis or database)
const sessions = new Map<string, any>();

// Generate simple session ID
function generateSessionId(): string {
  return Math.random().toString(36).substring(2) + Date.now().toString(36);
}

// Get session from cookie
function getSession(req: VercelRequest) {
  const cookies = req.headers.cookie || '';
  const sessionMatch = cookies.match(/sessionId=([^;]+)/);
  const sessionId = sessionMatch ? sessionMatch[1] : null;
  return sessionId ? sessions.get(sessionId) : null;
}

// Set session cookie
function setSession(res: VercelResponse, sessionData: any) {
  const sessionId = generateSessionId();
  sessions.set(sessionId, sessionData);
  res.setHeader('Set-Cookie', `sessionId=${sessionId}; HttpOnly; Path=/; Max-Age=86400; SameSite=Lax`);
  return sessionId;
}

// Destroy session
function destroySession(req: VercelRequest, res: VercelResponse) {
  const cookies = req.headers.cookie || '';
  const sessionMatch = cookies.match(/sessionId=([^;]+)/);
  const sessionId = sessionMatch ? sessionMatch[1] : null;
  if (sessionId) {
    sessions.delete(sessionId);
    res.setHeader('Set-Cookie', `sessionId=; HttpOnly; Path=/; Max-Age=0; SameSite=Lax`);
  }
}

export default async function handler(req: VercelRequest, res: VercelResponse) {
  try {
    console.log(`üîÑ Auth endpoint: ${req.method} ${req.url}`);

    // Add CORS headers
    res.setHeader('Access-Control-Allow-Credentials', 'true');
    res.setHeader('Access-Control-Allow-Origin', '*');
    res.setHeader('Access-Control-Allow-Methods', 'GET,OPTIONS,PATCH,DELETE,POST,PUT');
    res.setHeader('Access-Control-Allow-Headers', 'X-CSRF-Token, X-Requested-With, Accept, Accept-Version, Content-Length, Content-MD5, Content-Type, Date, X-Api-Version');

    // Handle preflight requests
    if (req.method === 'OPTIONS') {
      res.status(200).end();
      return;
    }

    // Handle login request
    if (req.method === 'POST') {
      const { username, password } = req.body || {};

      console.log('Login attempt:', { username, password: password ? '***' : 'missing', url: req.url, body: req.body });

      if (!username || !password) {
        return res.status(400).json({
          message: "Username and password are required",
          received: { username: !!username, password: !!password }
        });
      }

      // Check if this is admin login
      const isAdminLogin = req.url?.includes('/admin/login');

      if (isAdminLogin) {
        // Admin login logic
        if (username === 'admin' && password === 'admin123') {
          console.log('‚úÖ Admin login successful');
          const user = {
            id: 'demo-admin-1',
            username: 'admin',
            email: 'admin@demo.com',
            role: 'admin',
            walletAddress: null
          };

          // Store user in session
          setSession(res, { user });

          return res.status(200).json({
            user: user,
            message: "Admin login successful",
            token: 'demo-admin-jwt-token'
          });
        }
        return res.status(401).json({ message: "Invalid admin credentials" });
      } else {
        // User login logic
        if (username === 'trader1' && password === 'password123') {
          const user = {
            id: 'demo-user-1',
            username: 'trader1',
            email: 'trader1@demo.com',
            role: 'user',
            walletAddress: null
          };

          // Store user in session
          setSession(res, { user });

          return res.status(200).json({
            user: user,
            message: "Login successful"
          });
        }
        return res.status(401).json({ message: "Invalid user credentials" });
      }

      // Legacy fallback for admin credentials in user login (remove this later)
      if (username === 'admin' && password === 'admin123') {
        return res.status(200).json({
          user: {
            id: 'demo-admin-1',
            username: 'admin',
            email: 'admin@demo.com',
            role: 'admin',
            walletAddress: null
          },
          message: "Login successful",
          token: 'demo-jwt-token'
        });
      }

      return res.status(401).json({ message: "Invalid credentials" });
    }

    // Handle logout request
    if (req.method === 'POST' && req.url?.includes('logout')) {
      console.log('üîÑ Handling logout request in auth.ts');

      // Destroy session
      destroySession(req, res);

      return res.status(200).json({
        message: "Logout successful"
      });
    }

    // Handle GET request (check auth status)
    if (req.method === 'GET') {
      const session = getSession(req);
      const user = session?.user || null;
      return res.status(200).json(user);
    }

    return res.status(405).json({ message: "Method not allowed" });
  } catch (error) {
    console.error('‚ùå Auth endpoint error:', error);
    return res.status(500).json({
      error: 'Authentication failed',
      message: error instanceof Error ? error.message : 'Unknown error',
      timestamp: new Date().toISOString()
    });
  }
}

