import axios from 'axios';
import { storage } from './storage';

interface PriceData {
  symbol: string;
  price: string;
  priceChange24h: string;
  priceChangePercent24h: string;
  high24h: string;
  low24h: string;
  volume24h: string;
}

class PriceService {
  private updateInterval: NodeJS.Timeout | null = null;
  private readonly UPDATE_INTERVAL = 5000; // 5 seconds

  // Binance API for real-time prices
  async fetchBinancePrices(): Promise<PriceData[]> {
    try {
      const response = await axios.get('https://api.binance.com/api/v3/ticker/24hr');
      const data = response.data;

      // Filter for major trading pairs
      const majorPairs = ['BTCUSDT', 'ETHUSDT', 'BNBUSDT', 'ADAUSDT', 'SOLUSDT'];

      return data
        .filter((ticker: any) => majorPairs.includes(ticker.symbol))
        .map((ticker: any) => ({
          symbol: ticker.symbol,
          price: ticker.lastPrice,
          priceChange24h: ticker.priceChange,
          priceChangePercent24h: ticker.priceChangePercent,
          high24h: ticker.highPrice,
          low24h: ticker.lowPrice,
          volume24h: ticker.volume,
        }));
    } catch (error) {
      console.error('Error fetching Binance prices:', error);
      return this.getFallbackPrices();
    }
  }

  // Fallback mock prices if API fails
  getFallbackPrices(): PriceData[] {
    return [
      {
        symbol: 'BTCUSDT',
        price: '45000.00',
        priceChange24h: '1200.50',
        priceChangePercent24h: '2.74',
        high24h: '46500.00',
        low24h: '43800.00',
        volume24h: '28547.85',
      },
      {
        symbol: 'ETHUSDT',
        price: '3200.00',
        priceChange24h: '85.30',
        priceChangePercent24h: '2.74',
        high24h: '3350.00',
        low24h: '3100.00',
        volume24h: '185647.25',
      },
      {
        symbol: 'BNBUSDT',
        price: '320.50',
        priceChange24h: '12.80',
        priceChangePercent24h: '4.16',
        high24h: '335.00',
        low24h: '305.20',
        volume24h: '2547896.30',
      },
    ];
  }

  // Update market data in database
  async updateMarketData(): Promise<void> {
    try {
      const prices = await this.fetchBinancePrices();

      for (const priceData of prices) {
        await storage.updateMarketData(priceData.symbol, {
          price: priceData.price,
          priceChange24h: priceData.priceChange24h,
          priceChangePercent24h: priceData.priceChangePercent24h,
          high24h: priceData.high24h,
          low24h: priceData.low24h,
          volume24h: priceData.volume24h,
        });
      }

      console.log(`Updated market data for ${prices.length} symbols`);
    } catch (error) {
      console.error('Error updating market data:', error);
    }
  }

  // Start automatic price updates
  startPriceUpdates(): void {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
    }

    // Initial update
    this.updateMarketData();

    // Set up recurring updates
    this.updateInterval = setInterval(() => {
      this.updateMarketData();
    }, this.UPDATE_INTERVAL);

    console.log(`Started price updates every ${this.UPDATE_INTERVAL / 1000} seconds`);
  }

  // Stop automatic price updates
  stopPriceUpdates(): void {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
      this.updateInterval = null;
      console.log('Stopped price updates');
    }
  }

  // Get current price for a specific symbol
  async getCurrentPrice(symbol: string): Promise<string | null> {
    try {
      const marketData = await storage.getMarketData(symbol);
      return marketData?.price || null;
    } catch (error) {
      console.error(`Error getting current price for ${symbol}:`, error);
      return null;
    }
  }

  // Simulate price movement for admin-controlled trades
  simulatePriceMovement(currentPrice: string, direction: 'up' | 'down', percentage: number = 0.1): string {
    const price = parseFloat(currentPrice);
    const change = price * (percentage / 100);

    if (direction === 'up') {
      return (price + change).toFixed(8);
    } else {
      return (price - change).toFixed(8);
    }
  }
}

export const priceService = new PriceService();

