import { Switch, Route, useLocation } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { Navigation } from "./components/ui/navigation";
import { Footer } from "./components/ui/footer";
import { useAuth } from "@/hooks/useAuth";
import HomePage from "@/pages/HomePage";
import MarketsPage from "@/pages/MarketsPage";
import TradePage from "@/pages/TradePage";
import TradingPage from "@/pages/TradingPage";
import WalletPage from "@/pages/WalletPage";
import SupportPage from "@/pages/SupportPage";
import AdminDashboard from "@/pages/AdminDashboard";
import AdminLogin from "@/pages/AdminLogin";
import UserLogin from "@/pages/UserLogin";
import SignupPage from "@/pages/SignupPage";
import UserDashboard from "@/pages/UserDashboard";
import TransactionHistory from "@/pages/TransactionHistory";
import AdminTransactionsPage from "@/pages/AdminTransactionsPage";
import NotFound from "@/pages/not-found";
import { ProtectedAdminRoute } from "@/components/ProtectedAdminRoute";
import { ProtectedUserRoute } from "@/components/ProtectedUserRoute";

function Router() {
  const { user } = useAuth();
  const [location] = useLocation();
  const isAdmin = user?.role === 'admin' || user?.role === 'super_admin';

  // Pages that should not have navigation/footer
  const noLayoutPages = ['/login', '/signup', '/admin/login'];
  const shouldShowLayout = !noLayoutPages.includes(location);

  return (
    <div className="min-h-screen bg-gray-900">
      {shouldShowLayout && <Navigation />}
      <Switch>
        <Route path="/" component={HomePage} />
        <Route path="/dashboard">
          <ProtectedUserRoute>
            <UserDashboard />
          </ProtectedUserRoute>
        </Route>
        <Route path="/market" component={MarketsPage} />
        <Route path="/trading">
          <ProtectedUserRoute>
            <TradingPage />
          </ProtectedUserRoute>
        </Route>
        <Route path="/trade/spot">
          <ProtectedUserRoute>
            <TradePage type="spot" />
          </ProtectedUserRoute>
        </Route>
        <Route path="/trade/options">
          <ProtectedUserRoute>
            <TradePage type="options" />
          </ProtectedUserRoute>
        </Route>
        <Route path="/trade/:type?">
          <ProtectedUserRoute>
            <TradePage />
          </ProtectedUserRoute>
        </Route>
        <Route path="/wallet/:tab?">
          <ProtectedUserRoute>
            <WalletPage />
          </ProtectedUserRoute>
        </Route>
        <Route path="/wallet/history">
          <ProtectedUserRoute>
            <TransactionHistory />
          </ProtectedUserRoute>
        </Route>
        <Route path="/transactions">
          <ProtectedUserRoute>
            <TransactionHistory />
          </ProtectedUserRoute>
        </Route>
        <Route path="/support" component={SupportPage} />
        <Route path="/login" component={UserLogin} />
        <Route path="/signup" component={SignupPage} />
        <Route path="/admin/login" component={AdminLogin} />
        <Route path="/admin">
          <ProtectedAdminRoute>
            <AdminDashboard />
          </ProtectedAdminRoute>
        </Route>
        <Route path="/admin/transactions">
          <ProtectedAdminRoute>
            <AdminTransactionsPage />
          </ProtectedAdminRoute>
        </Route>
        <Route component={NotFound} />
      </Switch>
      {shouldShowLayout && <Footer />}
    </div>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

