import type { VercelRequest, VercelResponse } from '@vercel/node';

export default async function handler(req: VercelRequest, res: VercelResponse) {
  try {
    console.log(`üîÑ Admin Login: ${req.method} ${req.url}`);

    // Add CORS headers
    res.setHeader('Access-Control-Allow-Credentials', 'true');
    res.setHeader('Access-Control-Allow-Origin', '*');
    res.setHeader('Access-Control-Allow-Methods', 'GET,OPTIONS,PATCH,DELETE,POST,PUT');
    res.setHeader('Access-Control-Allow-Headers', 'X-CSRF-Token, X-Requested-With, Accept, Accept-Version, Content-Length, Content-MD5, Content-Type, Date, X-Api-Version, Authorization');

    // Handle preflight requests
    if (req.method === 'OPTIONS') {
      res.status(200).end();
      return;
    }

    if (req.method !== 'POST') {
      return res.status(405).json({ message: 'Method not allowed' });
    }

    const { username, password } = req.body;

    if (!username || !password) {
      return res.status(400).json({ message: "Username and password are required" });
    }

    // Demo admin credentials check
    if (username === 'admin' && password === 'admin123') {
      console.log('‚úÖ Admin login successful');

      // Set a simple session cookie
      res.setHeader('Set-Cookie', 'admin-session=demo-admin-session; HttpOnly; Path=/; Max-Age=86400');

      return res.status(200).json({
        user: {
          id: 'demo-admin-1',
          username: 'admin',
          email: 'admin@demo.com',
          role: 'admin',
          walletAddress: null
        },
        message: "Admin login successful",
        token: 'demo-admin-jwt-token'
      });
    }

    return res.status(401).json({ message: "Invalid admin credentials" });

  } catch (error) {
    console.error('‚ùå Admin login endpoint error:', error);
    return res.status(500).json({
      error: 'Internal server error',
      message: error instanceof Error ? error.message : 'Unknown error',
      timestamp: new Date().toISOString()
    });
  }
}

