import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { Wallet, AlertTriangle, CheckCircle, Copy } from 'lucide-react';

declare global {
  interface Window {
    ethereum?: any;
  }
}

interface MetaMaskConnectProps {
  onConnect?: (address: string) => void;
  className?: string;
}

export default function MetaMaskConnect({ onConnect, className }: MetaMaskConnectProps) {
  const [account, setAccount] = useState<string | null>(null);
  const [isConnecting, setIsConnecting] = useState(false);
  const [chainId, setChainId] = useState<string | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // MetaMask authentication mutation
  const authMutation = useMutation({
    mutationFn: async (data: { walletAddress: string; signature?: string }) => {
      return await apiRequest('POST', '/api/auth/metamask', data);
    },
    onSuccess: (data) => {
      toast({
        title: 'Connected Successfully',
        description: 'Your MetaMask wallet has been connected.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/auth/user'] });
      if (onConnect) {
        onConnect(account!);
      }
    },
    onError: (error) => {
      toast({
        title: 'Connection Failed',
        description: error.message || 'Failed to connect wallet.',
        variant: 'destructive',
      });
    },
  });

  // Check if MetaMask is installed
  const isMetaMaskInstalled = () => {
    return typeof window !== 'undefined' && window.ethereum && window.ethereum.isMetaMask;
  };

  // Get current account
  const getCurrentAccount = async () => {
    try {
      const accounts = await window.ethereum!.request({ method: 'eth_accounts' });
      if (accounts.length > 0) {
        setAccount(accounts[0]);
        const chainId = await window.ethereum!.request({ method: 'eth_chainId' });
        setChainId(chainId);
      }
    } catch (error) {
      console.error('Error getting current account:', error);
    }
  };

  // Connect to MetaMask
  const connectWallet = async () => {
    if (!isMetaMaskInstalled()) {
      toast({
        title: 'MetaMask Not Found',
        description: 'Please install MetaMask to connect your wallet.',
        variant: 'destructive',
      });
      return;
    }

    setIsConnecting(true);
    try {
      // Request account access
      const accounts = await window.ethereum!.request({
        method: 'eth_requestAccounts',
      });

      if (accounts.length > 0) {
        const walletAddress = accounts[0];
        setAccount(walletAddress);

        // Get chain ID
        const chainId = await window.ethereum!.request({ method: 'eth_chainId' });
        setChainId(chainId);

        // Authenticate with backend
        await authMutation.mutateAsync({ walletAddress });
      }
    } catch (error: any) {
      console.error('Error connecting to MetaMask:', error);
      toast({
        title: 'Connection Failed',
        description: error.message || 'Failed to connect to MetaMask.',
        variant: 'destructive',
      });
    } finally {
      setIsConnecting(false);
    }
  };

  // Sign message for additional verification (optional)
  const signMessage = async () => {
    if (!account) return;

    try {
      const message = `Sign this message to verify your wallet ownership: ${Date.now()}`;
      const signature = await window.ethereum!.request({
        method: 'personal_sign',
        params: [message, account],
      });

      // Send signature to backend for additional verification
      await authMutation.mutateAsync({ walletAddress: account, signature });
    } catch (error: any) {
      console.error('Error signing message:', error);
      toast({
        title: 'Signing Failed',
        description: error.message || 'Failed to sign message.',
        variant: 'destructive',
      });
    }
  };

  // Handle account changes
  const handleAccountsChanged = (accounts: string[]) => {
    if (accounts.length > 0) {
      setAccount(accounts[0]);
    } else {
      setAccount(null);
      setChainId(null);
    }
  };

  // Handle chain changes
  const handleChainChanged = (chainId: string) => {
    setChainId(chainId);
  };

  // Setup event listeners
  useEffect(() => {
    if (isMetaMaskInstalled()) {
      getCurrentAccount();

      window.ethereum!.on('accountsChanged', handleAccountsChanged);
      window.ethereum!.on('chainChanged', handleChainChanged);

      return () => {
        window.ethereum!.removeListener('accountsChanged', handleAccountsChanged);
        window.ethereum!.removeListener('chainChanged', handleChainChanged);
      };
    }
  }, []);

  // Get network name from chain ID
  const getNetworkName = (chainId: string) => {
    const networks: { [key: string]: string } = {
      '0x1': 'Ethereum Mainnet',
      '0x3': 'Ropsten Testnet',
      '0x4': 'Rinkeby Testnet',
      '0x5': 'Goerli Testnet',
      '0x89': 'Polygon Mainnet',
      '0x13881': 'Polygon Mumbai Testnet',
      '0x38': 'BSC Mainnet',
      '0x61': 'BSC Testnet',
    };
    return networks[chainId] || `Unknown (${chainId})`;
  };

  // Copy address to clipboard
  const copyAddress = async () => {
    if (account) {
      try {
        await navigator.clipboard.writeText(account);
        toast({
          title: 'Copied',
          description: 'Wallet address copied to clipboard.',
        });
      } catch (error) {
        console.error('Failed to copy address:', error);
      }
    }
  };

  if (!isMetaMaskInstalled()) {
    return (
      <Card className={`bg-slate-800/90 border-red-500/20 ${className}`}>
        <CardContent className="p-6 text-center">
          <AlertTriangle className="w-12 h-12 text-red-400 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-white mb-2">MetaMask Required</h3>
          <p className="text-gray-300 mb-4">
            Please install MetaMask to connect your wallet and start trading.
          </p>
          <Button
            onClick={() => window.open('https://metamask.io/download/', '_blank')}
            className="bg-orange-600 hover:bg-orange-700 text-white"
          >
            Install MetaMask
          </Button>
        </CardContent>
      </Card>
    );
  }

  if (account) {
    return (
      <Card className={`bg-slate-800/90 border-green-500/20 ${className}`}>
        <CardHeader>
          <CardTitle className="text-white flex items-center gap-2">
            <CheckCircle className="w-5 h-5 text-green-400" />
            Wallet Connected
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center justify-between bg-slate-700/50 p-3 rounded-lg">
            <div>
              <p className="text-sm text-gray-300">Address</p>
              <p className="text-white font-mono text-sm">
                {account.slice(0, 6)}...{account.slice(-4)}
              </p>
            </div>
            <Button
              size="sm"
              variant="outline"
              onClick={copyAddress}
              className="border-purple-500 text-purple-400 hover:bg-purple-500/20"
            >
              <Copy className="w-4 h-4" />
            </Button>
          </div>

          {chainId && (
            <div className="bg-slate-700/50 p-3 rounded-lg">
              <p className="text-sm text-gray-300">Network</p>
              <Badge variant="outline" className="border-blue-500 text-blue-400">
                {getNetworkName(chainId)}
              </Badge>
            </div>
          )}

          <div className="flex gap-2">
            <Button
              onClick={signMessage}
              disabled={authMutation.isPending}
              size="sm"
              className="flex-1 bg-purple-600 hover:bg-purple-700"
            >
              Verify Signature
            </Button>
            <Button
              onClick={() => {
                setAccount(null);
                setChainId(null);
              }}
              size="sm"
              variant="outline"
              className="border-red-500 text-red-400 hover:bg-red-500/20"
            >
              Disconnect
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={`bg-slate-800/90 border-purple-500/20 ${className}`}>
      <CardHeader>
        <CardTitle className="text-white flex items-center gap-2">
          <Wallet className="w-5 h-5 text-purple-400" />
          Connect MetaMask
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <p className="text-gray-300 text-sm">
          Connect your MetaMask wallet to start trading and manage your funds securely.
        </p>

        <Button
          onClick={connectWallet}
          disabled={isConnecting || authMutation.isPending}
          className="w-full bg-orange-600 hover:bg-orange-700 text-white h-12"
        >
          <Wallet className="w-5 h-5 mr-2" />
          {isConnecting || authMutation.isPending ? 'Connecting...' : 'Connect MetaMask'}
        </Button>

        <div className="text-xs text-gray-400 space-y-1">
          <p>‚Ä¢ Secure wallet connection</p>
          <p>‚Ä¢ No private keys stored</p>
          <p>‚Ä¢ Decentralized authentication</p>
        </div>
      </CardContent>
    </Card>
  );
}

