client/src/components/StripePayment.tsx.content:
import { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements
} from '@stripe/react-stripe-js';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { stripeService } from '@/services/stripeService';

const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLISHABLE_KEY || 'pk_test_your_key_here');

interface PaymentFormProps {
  amount: string;
  currency: string;
  onSuccess: (paymentIntentId: string) => void;
  onError: (error: string) => void;
}

function PaymentForm({ amount, currency, onSuccess, onError }: PaymentFormProps) {
  const stripe = useStripe();
  const elements = useElements();
  const { toast } = useToast();
  const [isProcessing, setIsProcessing] = useState(false);
  const [clientSecret, setClientSecret] = useState<string>('');

  useEffect(() => {
    // Create payment intent when component mounts
    const createPaymentIntent = async () => {
      try {
        const { clientSecret } = await stripeService.createPaymentIntent(amount, currency);
        setClientSecret(clientSecret);
      } catch (error) {
        onError(error instanceof Error ? error.message : 'Failed to initialize payment');
      }
    };

    createPaymentIntent();
  }, [amount, currency, onError]);

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!stripe || !elements || !clientSecret) {
      return;
    }

    setIsProcessing(true);

    const cardElement = elements.getElement(CardElement);
    if (!cardElement) {
      setIsProcessing(false);
      return;
    }

    try {
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
        }
      });

      if (error) {
        onError(error.message || 'Payment failed');
        toast({
          title: 'Payment Failed',
          description: error.message,
          variant: 'destructive',
        });
      } else if (paymentIntent.status === 'succeeded') {
        onSuccess(paymentIntent.id);
        toast({
          title: 'Payment Successful',
          description: `Successfully processed payment of ${amount} ${currency.toUpperCase()}`,
        });
      }
    } catch (error) {
      onError(error instanceof Error ? error.message : 'Payment processing failed');
    } finally {
      setIsProcessing(false);
    }
  };

  const cardElementOptions = {
    style: {
      base: {
        fontSize: '16px',
        color: '#ffffff',
        backgroundColor: 'transparent',
        '::placeholder': {
          color: '#9ca3af',
        },
      },
      invalid: {
        color: '#ef4444',
      },
    },
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <Label className="text-gray-300 mb-2 block">Card Information</Label>
        <div className="p-3 border border-gray-600 rounded-lg bg-gray-700">
          <CardElement options={cardElementOptions} />
        </div>
      </div>

      <div className="text-sm text-gray-400">
        <p>Amount: {amount} {currency.toUpperCase()}</p>
        <p className="mt-1">Your payment is secured by Stripe</p>
      </div>

      <Button
        type="submit"
        disabled={!stripe || isProcessing || !clientSecret}
        className="w-full bg-green-600 hover:bg-green-700"
      >
        {isProcessing ? 'Processing...' : `Pay ${amount} ${currency.toUpperCase()}`}
      </Button>
    </form>
  );
}

interface StripePaymentProps {
  amount: string;
  currency: string;
  onSuccess: (paymentIntentId: string) => void;
  onError: (error: string) => void;
}

export default function StripePayment({ amount, currency, onSuccess, onError }: StripePaymentProps) {
  return (
    <Elements stripe={stripePromise}>
      <PaymentForm 
        amount={amount} 
        currency={currency} 
        onSuccess={onSuccess} 
        onError={onError} 
      />
    </Elements>
  );
}

client/src/components/TradingChart.tsx.content:
import { useEffect, useRef, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";
import { Expand } from "lucide-react";
import type { MarketData } from "@shared/schema";

interface TradingChartProps {
  symbol: string;
  height?: number;
}

export default function TradingChart({ symbol, height = 400 }: TradingChartProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [timeframe, setTimeframe] = useState("15m");
  const [chartData, setChartData] = useState<any[]>([]);

  const { data: marketData } = useQuery<MarketData[]>({
    queryKey: ["/api/market-data"],
  });

  const currentPrice = marketData?.find(data => data.symbol === symbol)?.price || "0";

  // Generate sample OHLCV data for demonstration
  useEffect(() => {
    const generateChartData = () => {
      const data = [];
      const basePrice = parseFloat(currentPrice) || 113812;
      let price = basePrice;

      for (let i = 0; i < 50; i++) {
        const change = (Math.random() - 0.5) * 200;
        const open = price;
        const close = price + change;
        const high = Math.max(open, close) + Math.random() * 100;
        const low = Math.min(open, close) - Math.random() * 100;
        const volume = Math.random() * 1000;

        data.push({
          time: new Date(Date.now() - (49 - i) * 15 * 60 * 1000), // 15 minute intervals
          open,
          high,
          low,
          close,
          volume
        });

        price = close;
      }

      setChartData(data);
    };

    if (currentPrice !== "0") {
      generateChartData();
    }
  }, [currentPrice, timeframe]);

  // Draw candlestick chart
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas || chartData.length === 0) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    const { width, height } = canvas;
    ctx.clearRect(0, 0, width, height);

    // Calculate price range
    const prices = chartData.flatMap(d => [d.high, d.low]);
    const minPrice = Math.min(...prices);
    const maxPrice = Math.max(...prices);
    const priceRange = maxPrice - minPrice;
    const padding = priceRange * 0.1;

    // Chart dimensions
    const chartHeight = height - 60;
    const chartWidth = width - 80;
    const barWidth = chartWidth / chartData.length;

    // Draw grid lines
    ctx.strokeStyle = "rgba(255, 255, 255, 0.1)";
    ctx.lineWidth = 1;

    // Horizontal grid lines
    for (let i = 0; i <= 5; i++) {
      const y = 30 + (chartHeight / 5) * i;
      ctx.beginPath();
      ctx.moveTo(40, y);
      ctx.lineTo(width - 40, y);
      ctx.stroke();

      // Price labels
      const price = maxPrice + padding - ((maxPrice + padding - (minPrice - padding)) / 5) * i;
      ctx.fillStyle = "#9CA3AF";
      ctx.font = "10px Inter";
      ctx.textAlign = "right";
      ctx.fillText(price.toFixed(2), 35, y + 3);
    }

    // Vertical grid lines
    for (let i = 0; i < chartData.length; i += 10) {
      const x = 40 + barWidth * i;
      ctx.beginPath();
      ctx.moveTo(x, 30);
      ctx.lineTo(x, height - 30);
      ctx.stroke();
    }

    // Draw candlesticks
    chartData.forEach((data, index) => {
      const x = 40 + barWidth * index;
      const openY = 30 + ((maxPrice + padding - data.open) / (maxPrice + padding - (minPrice - padding))) * chartHeight;
      const closeY = 30 + ((maxPrice + padding - data.close) / (maxPrice + padding - (minPrice - padding))) * chartHeight;
      const highY = 30 + ((maxPrice + padding - data.high) / (maxPrice + padding - (minPrice - padding))) * chartHeight;
      const lowY = 30 + ((maxPrice + padding - data.low) / (maxPrice + padding - (minPrice - padding))) * chartHeight;

      const isGreen = data.close > data.open;
      const color = isGreen ? "#10B981" : "#EF4444";

      // Draw wick
      ctx.strokeStyle = color;
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.moveTo(x + barWidth / 2, highY);
      ctx.lineTo(x + barWidth / 2, lowY);
      ctx.stroke();

      // Draw body
      ctx.fillStyle = color;
      const bodyTop = Math.min(openY, closeY);
      const bodyHeight = Math.abs(closeY - openY);
      ctx.fillRect(x + 2, bodyTop, barWidth - 4, Math.max(bodyHeight, 1));
    });

    // Draw current price line
    if (chartData.length > 0) {
      const lastPrice = parseFloat(currentPrice);
      const priceY = 30 + ((maxPrice + padding - lastPrice) / (maxPrice + padding - (minPrice - padding))) * chartHeight;

      ctx.strokeStyle = "#8B5CF6";
      ctx.lineWidth = 2;
      ctx.setLineDash([5, 5]);
      ctx.beginPath();
      ctx.moveTo(40, priceY);
      ctx.lineTo(width - 40, priceY);
      ctx.stroke();
      ctx.setLineDash([]);

      // Price label
      ctx.fillStyle = "#8B5CF6";
      ctx.fillRect(width - 80, priceY - 10, 75, 20);
      ctx.fillStyle = "white";
      ctx.font = "12px Inter";
      ctx.textAlign = "center";
      ctx.fillText(lastPrice.toFixed(2), width - 42.5, priceY + 4);
    }
  }, [chartData, currentPrice]);

  const timeframes = [
    { label: "15m", value: "15m" },
    { label: "1h", value: "1h" },
    { label: "4h", value: "4h" },
    { label: "1D", value: "1D" },
    { label: "1W", value: "1W" },
  ];

  return (
    <Card className="card-dark">
