import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import type { User } from "@shared/schema-sqlite";

export function useAuth() {
  const queryClient = useQueryClient();

  const { data: user, isLoading, error } = useQuery<User | null>({
    queryKey: ["/api/auth/user"],
    retry: false,
    staleTime: 5 * 60 * 1000, // 5 minutes
    refetchOnWindowFocus: false,
  });

  const userLoginMutation = useMutation({
    mutationFn: async (credentials: { username: string; password: string }) => {
      const response = await apiRequest("POST", "/api/auth/user/login", credentials);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.setQueryData(["/api/auth/user"], data.user);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
    },
  });

  const adminLoginMutation = useMutation({
    mutationFn: async (credentials: { username: string; password: string }) => {
      const response = await apiRequest("POST", "/api/auth/admin/login", credentials);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.setQueryData(["/api/auth/user"], data.user);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
    },
  });

  const logoutMutation = useMutation({
    mutationFn: async () => {
      try {
        // Try to call logout endpoint
        await apiRequest("POST", "/api/auth/user/logout");
      } catch (error) {
        console.warn("Logout API failed, continuing with local cleanup:", error);
      }
    },
    onSuccess: () => {
      // Always clear local state regardless of API response
      queryClient.setQueryData(["/api/auth/user"], null);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });

      // Clear any stored auth data
      localStorage.removeItem('authToken');
      localStorage.removeItem('user');
      sessionStorage.clear();

      // Force redirect to home page
      window.location.href = "/";
    },
    onError: () => {
      // Even if logout fails, clear local state and redirect
      queryClient.setQueryData(["/api/auth/user"], null);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });

      localStorage.removeItem('authToken');
      localStorage.removeItem('user');
      sessionStorage.clear();

      window.location.href = "/";
    },
  });

  const registerMutation = useMutation({
    mutationFn: async (userData: {
      username: string;
      email: string;
      password: string;
    }) => {
      const response = await apiRequest("POST", "/api/auth/user/register", userData);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.setQueryData(["/api/auth/user"], data.user);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
    },
  });

  const metamaskLoginMutation = useMutation({
    mutationFn: async (data: { walletAddress: string; signature?: string }) => {
      const response = await apiRequest("POST", "/api/auth/metamask", data);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.setQueryData(["/api/auth/user"], data.user);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
    },
  });

  return {
    user,
    isLoading,
    isAuthenticated: !!user,
    error,
    userLogin: userLoginMutation.mutateAsync,
    adminLogin: adminLoginMutation.mutateAsync,
    logout: logoutMutation.mutateAsync,
    register: registerMutation.mutateAsync,
    metamaskLogin: metamaskLoginMutation.mutateAsync,
    isUserLoginPending: userLoginMutation.isPending,
    isAdminLoginPending: adminLoginMutation.isPending,
    isLogoutPending: logoutMutation.isPending,
    isRegisterPending: registerMutation.isPending,
    isMetamaskLoginPending: metamaskLoginMutation.isPending,
  };
}

client/src/hooks/useMetamask.tsx.content:
import { useState, useCallback } from "react";
import { useAuth } from "./useAuth";

declare global {
  interface Window {
    ethereum?: any;
  }
}

interface MetamaskResult {
  success: boolean;
  address?: string;
  error?: string;
}

export function useMetamask() {
  const [isConnecting, setIsConnecting] = useState(false);
  const { metamaskLogin } = useAuth();

  const isMetamaskAvailable = useCallback(() => {
    return typeof window !== "undefined" && typeof window.ethereum !== "undefined";
  }, []);

  const connectWallet = useCallback(async (): Promise<MetamaskResult> => {
    if (!isMetamaskAvailable()) {
      return {
        success: false,
        error: "Metamask is not installed. Please install Metamask to continue.",
      };
    }

    try {
      setIsConnecting(true);

      // Request account access
      const accounts = await window.ethereum.request({
        method: "eth_requestAccounts",
      });

      if (!accounts || accounts.length === 0) {
        return {
          success: false,
          error: "No accounts found. Please make sure Metamask is unlocked.",
        };
      }

      const address = accounts[0];

      // Get chain ID to ensure we're on the right network
      const chainId = await window.ethereum.request({
        method: "eth_chainId",
      });

      console.log("Connected to chain:", chainId);

      // Sign a message for authentication (optional)
      try {
        const message = `Welcome to METACHROME! Please sign this message to authenticate your wallet: ${Date.now()}`;
        const signature = await window.ethereum.request({
          method: "personal_sign",
          params: [message, address],
        });

        // Send to backend for authentication
        await metamaskLogin({ walletAddress: address, signature });
      } catch (signError) {
        console.error("Signature error:", signError);
        // Still proceed with basic authentication without signature
        await metamaskLogin({ walletAddress: address });
      }

      return {
        success: true,
        address,
      };
    } catch (error: any) {
      console.error("Metamask connection error:", error);

      let errorMessage = "Failed to connect to Metamask";

      if (error.code === 4001) {
        errorMessage = "User rejected the connection request";
      } else if (error.code === -32002) {
        errorMessage = "Connection request is already pending. Please check Metamask.";
      } else if (error.message) {
        errorMessage = error.message;
      }

      return {
        success: false,
        error: errorMessage,
      };
    } finally {
      setIsConnecting(false);
    }
  }, [isMetamaskAvailable, metamaskLogin]);

  const switchNetwork = useCallback(async (chainId: string) => {
    if (!isMetamaskAvailable()) {
      throw new Error("Metamask is not available");
    }

    try {
      await window.ethereum.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId }],
      });
    } catch (error: any) {
      // If the network doesn't exist, add it
      if (error.code === 4902) {
        throw new Error("Network not found. Please add it manually in Metamask.");
      }
      throw error;
    }
  }, [isMetamaskAvailable]);

  const addToken = useCallback(async (tokenAddress: string, tokenSymbol: string, tokenDecimals: number) => {
    if (!isMetamaskAvailable()) {
      throw new Error("Metamask is not available");
    }

    try {
      await window.ethereum.request({
        method: "wallet_watchAsset",
        params: {
          type: "ERC20",
          options: {
            address: tokenAddress,
            symbol: tokenSymbol,
            decimals: tokenDecimals,
          },
        },
      });
    } catch (error) {
      console.error("Error adding token:", error);
      throw error;
    }
  }, [isMetamaskAvailable]);

  const getBalance = useCallback(async (address: string) => {
    if (!isMetamaskAvailable()) {
      throw new Error("Metamask is not available");
    }

    try {
      const balance = await window.ethereum.request({
        method: "eth_getBalance",
        params: [address, "latest"],
      });

      // Convert from wei to ETH
      return parseInt(balance, 16) / Math.pow(10, 18);
    } catch (error) {
      console.error("Error getting balance:", error);
      throw error;
    }
  }, [isMetamaskAvailable]);

  return {
    isMetamaskAvailable: isMetamaskAvailable(),
    isConnecting,
    connectWallet,
    switchNetwork,
    addToken,
    getBalance,
  };
}

client/src/hooks/useWebSocket.tsx.content:
import { useState, useEffect, useRef, useCallback } from "react";

interface WebSocketMessage {
  type: string;
  data?: any;
}

export function useWebSocket() {
  const [socket, setSocket] = useState<WebSocket | null>(null);
  const [connected, setConnected] = useState(false);
  const [lastMessage, setLastMessage] = useState<WebSocketMessage | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout>();
  const reconnectAttemptsRef = useRef(0);
  const maxReconnectAttempts = 5;

  const connect = useCallback(() => {
    try {
      const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
      const wsUrl = `${protocol}//${window.location.host}/ws`;
      const ws = new WebSocket(wsUrl);

      ws.onopen = () => {
        console.log("WebSocket connected");
        setConnected(true);
        reconnectAttemptsRef.current = 0;

        // Send ping to keep connection alive
        ws.send(JSON.stringify({ type: "ping" }));
      };

      ws.onmessage = (event) => {
        try {
          const message: WebSocketMessage = JSON.parse(event.data);
          setLastMessage(message);

          if (message.type === "pong") {
            // Handle pong response
            console.log("Received pong from server");
          }
        } catch (error) {
          console.error("Error parsing WebSocket message:", error);
        }
      };

      ws.onclose = (event) => {
        console.log("WebSocket disconnected:", event.code, event.reason);
        setConnected(false);
        setSocket(null);

        // Attempt to reconnect if not intentionally closed
        if (event.code !== 1000 && reconnectAttemptsRef.current < maxReconnectAttempts) {
          const delay = Math.min(1000 * Math.pow(2, reconnectAttemptsRef.current), 10000);
          console.log(`Attempting to reconnect in ${delay}ms...`);

          reconnectTimeoutRef.current = setTimeout(() => {
            reconnectAttemptsRef.current++;
            connect();
          }, delay);
        }
      };

      ws.onerror = (error) => {
        console.error("WebSocket error:", error);
      };

      setSocket(ws);
    } catch (error) {
      console.error("Failed to create WebSocket connection:", error);
    }
  }, []);

  const disconnect = useCallback(() => {
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
    }

    if (socket) {
      socket.close(1000, "Intentional disconnect");
    }
  }, [socket]);

  const sendMessage = useCallback((message: WebSocketMessage) => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.send(JSON.stringify(message));
    } else {
      console.warn("WebSocket is not connected. Message not sent:", message);
    }
  }, [socket]);

  const subscribe = useCallback((symbols: string[]) => {
    sendMessage({
      type: "subscribe",
      data: { symbols }
    });
  }, [sendMessage]);

  const unsubscribe = useCallback((symbols: string[]) => {
    sendMessage({
      type: "unsubscribe",
      data: { symbols }
    });
  }, [sendMessage]);

  // Connect on mount
  useEffect(() => {
    connect();

    // Cleanup on unmount
    return () => {
      disconnect();
    };
  }, []);

  // Keep-alive ping
  useEffect(() => {
    if (!connected) return;

    const pingInterval = setInterval(() => {
      sendMessage({ type: "ping" });
    }, 30000); // Ping every 30 seconds

    return () => clearInterval(pingInterval);
  }, [connected, sendMessage]);

  return {
    socket,
    connected,
    lastMessage,
    sendMessage,
    subscribe,
    unsubscribe,
    connect,
    disconnect,
  };
}

