                                    onClick={handleUpdateBalance}
                                    disabled={updateBalanceMutation.isPending}
                                    className="w-full bg-purple-600 hover:bg-purple-700"
                                  >
                                    Update Balance
                                  </Button>
                                </div>
                              </DialogContent>
                            </Dialog>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </CardContent>
              </Card>
            </TabsContent>
          )}
        </Tabs>

        {/* Control Details Dialog */}
        <Dialog open={showControlDetails} onOpenChange={setShowControlDetails}>
          <DialogContent className="bg-slate-800 border-purple-500/20 text-white">
            <DialogHeader>
              <DialogTitle>Control Details</DialogTitle>
            </DialogHeader>
            {selectedControl && (
              <div className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label className="text-gray-300">User</Label>
                    <p className="text-white font-medium">
                      {selectedControl.user?.username || selectedControl.user?.email || 'Unknown User'}
                    </p>
                  </div>
                  <div>
                    <Label className="text-gray-300">Current Control Type</Label>
                    <div className="mt-1">
                      {getControlBadge(selectedControl.controlType)}
                    </div>
                  </div>
                  <div>
                    <Label className="text-gray-300">Created</Label>
                    <p className="text-white">
                      {new Date(selectedControl.createdAt).toLocaleString()}
                    </p>
                  </div>
                  <div>
                    <Label className="text-gray-300">Status</Label>
                    <p className="text-white">
                      {selectedControl.isActive ? 'Active' : 'Inactive'}
                    </p>
                  </div>
                </div>

                <div className="space-y-3">
                  <Label className="text-gray-300">Change Control Type</Label>
                  <div className="flex gap-2">
                    <Button
                      onClick={() => handleUpdateControl('normal')}
                      disabled={updateControlMutation.isPending}
                      variant={selectedControl.controlType === 'normal' ? 'default' : 'outline'}
                      className="flex-1"
                    >
                      Normal
                    </Button>
                    <Button
                      onClick={() => handleUpdateControl('win')}
                      disabled={updateControlMutation.isPending}
                      variant={selectedControl.controlType === 'win' ? 'default' : 'outline'}
                      className="flex-1"
                    >
                      Force Win
                    </Button>
                    <Button
                      onClick={() => handleUpdateControl('lose')}
                      disabled={updateControlMutation.isPending}
                      variant={selectedControl.controlType === 'lose' ? 'default' : 'outline'}
                      className="flex-1"
                    >
                      Force Lose
                    </Button>
                  </div>
                </div>

                <div className="flex justify-between pt-4 border-t border-slate-600">
                  <Button
                    onClick={handleDeleteControl}
                    disabled={deleteControlMutation.isPending}
                    variant="destructive"
                  >
                    Delete Control
                  </Button>
                  <Button
                    onClick={() => setShowControlDetails(false)}
                    variant="outline"
                  >
                    Close
                  </Button>
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

client/src/pages/AdminLogin.tsx.content:
import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/hooks/useAuth';
import { useLocation } from 'wouter';
import { Lock, Shield } from 'lucide-react';

const loginSchema = z.object({
  username: z.string().min(1, 'Username is required'),
  password: z.string().min(1, 'Password is required'),
});

type LoginForm = z.infer<typeof loginSchema>;

export default function AdminLogin() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { user, adminLogin, isAdminLoginPending } = useAuth();

  // Redirect if already authenticated as admin
  useEffect(() => {
    if (user && (user.role === 'admin' || user.role === 'super_admin')) {
      setLocation('/admin');
    }
  }, [user, setLocation]);

  const form = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: 'admin',
      password: 'admin123',
    },
  });

  const onSubmit = async (data: LoginForm) => {
    try {
      await adminLogin(data);
      toast({
        title: "Login Successful",
        description: `Welcome back, ${data.username}!`,
      });
      // The useEffect will handle the redirect when user state updates
    } catch (error: any) {
      toast({
        title: "Login Failed",
        description: error.message || "Invalid admin credentials",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 flex items-center justify-center px-4">
      <div className="w-full max-w-md">
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-blue-500 rounded-lg flex items-center justify-center">
              <Shield className="text-white w-6 h-6" />
            </div>
          </div>
          <h1 className="text-2xl font-bold text-white">Admin Login</h1>
          <p className="text-gray-400 mt-2">Access the METACHROME admin dashboard</p>
        </div>

        <Card className="bg-gray-800 border-gray-700">
          <CardHeader>
            <CardTitle className="text-white">Sign In</CardTitle>
            <CardDescription className="text-gray-400">
              Enter your admin credentials to continue
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="username"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-white">Username</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          placeholder="Enter your username"
                          className="bg-gray-700 border-gray-600 text-white placeholder:text-gray-400"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-white">Password</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          type="password"
                          placeholder="Enter your password"
                          className="bg-gray-700 border-gray-600 text-white placeholder:text-gray-400"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700"
                  disabled={isAdminLoginPending}
                >
                  {isAdminLoginPending ? (
                    <>
                      <Lock className="mr-2 h-4 w-4 animate-spin" />
                      Signing In...
                    </>
                  ) : (
                    <>
                      <Lock className="mr-2 h-4 w-4" />
                      Sign In
                    </>
                  )}
                </Button>
              </form>
            </Form>

            <div className="mt-6 p-4 bg-gray-700/50 rounded-lg">
              <h3 className="text-sm font-medium text-white mb-2">Demo Credentials:</h3>
              <div className="text-xs text-gray-400 space-y-1">
                <p><strong>Username:</strong> admin</p>
                <p><strong>Password:</strong> admin123</p>
                <p className="mt-2 text-yellow-400">‚ö†Ô∏è For demonstration purposes only</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/AdminTransactionsPage.t.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { CheckCircle, XCircle, Clock, Eye } from "lucide-react";

interface Transaction {
  id: string;
  userId: string;
  type: string;
  symbol: string;
  amount: string;
  status: string;
  method: string;
  txHash?: string;
  metadata?: string;
  createdAt: string;
}

export default function AdminTransactionsPage() {
  const [selectedTransaction, setSelectedTransaction] = useState<Transaction | null>(null);
  const [rejectionReason, setRejectionReason] = useState('');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch pending transactions
  const { data: pendingTransactions, isLoading } = useQuery({
    queryKey: ["/api/admin/transactions/pending"],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Approve/reject transaction mutation
  const approveTransactionMutation = useMutation({
    mutationFn: async ({ id, action, reason }: { id: string; action: 'approve' | 'reject'; reason?: string }) => {
      const response = await fetch(`/api/admin/transactions/${id}/approve`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ action, reason }),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Action failed');
      }
      return response.json();
    },
    onSuccess: (data, variables) => {
      toast({
        title: variables.action === 'approve' ? 'Transaction Approved' : 'Transaction Rejected',
        description: `Transaction has been ${variables.action}d successfully`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/transactions/pending'] });
      setSelectedTransaction(null);
      setRejectionReason('');
    },
    onError: (error: any) => {
      toast({
        title: 'Action Failed',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="outline" className="text-yellow-600 border-yellow-600"><Clock className="w-3 h-3 mr-1" />Pending</Badge>;
      case 'completed':
        return <Badge variant="outline" className="text-green-600 border-green-600"><CheckCircle className="w-3 h-3 mr-1" />Completed</Badge>;
      case 'failed':
        return <Badge variant="outline" className="text-red-600 border-red-600"><XCircle className="w-3 h-3 mr-1" />Failed</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  const parseMetadata = (metadata?: string) => {
    try {
      return metadata ? JSON.parse(metadata) : {};
    } catch {
      return {};
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-900 text-white p-8">
        <div className="max-w-6xl mx-auto">
          <h1 className="text-3xl font-bold mb-8">Transaction Management</h1>
          <div className="text-center">Loading...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">Transaction Management</h1>
          <Badge variant="outline" className="text-yellow-600 border-yellow-600">
            {pendingTransactions?.length || 0} Pending
          </Badge>
        </div>

        {!pendingTransactions || pendingTransactions.length === 0 ? (
          <Card className="bg-gray-800 border-gray-700">
            <CardContent className="p-8 text-center">
              <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
              <h3 className="text-xl font-semibold mb-2">No Pending Transactions</h3>
              <p className="text-gray-400">All transactions have been processed.</p>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-4">
            {pendingTransactions.map((transaction: Transaction) => (
              <Card key={transaction.id} className="bg-gray-800 border-gray-700">
                <CardContent className="p-6">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="flex items-center gap-4 mb-2">
                        <h3 className="text-lg font-semibold">
                          {transaction.type.toUpperCase()} - {transaction.amount} {transaction.symbol}
                        </h3>
                        {getStatusBadge(transaction.status)}
                        <Badge variant="secondary">{transaction.method}</Badge>
                      </div>

                      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm text-gray-400 mb-4">
                        <div>
                          <span className="font-medium">User ID:</span>
                          <br />
                          {transaction.userId}
                        </div>
                        <div>
                          <span className="font-medium">Created:</span>
                          <br />
                          {formatDate(transaction.createdAt)}
                        </div>
                        <div>
                          <span className="font-medium">Method:</span>
                          <br />
                          {transaction.method}
                        </div>
                        <div>
                          <span className="font-medium">TX Hash:</span>
                          <br />
                          {transaction.txHash ? (
                            <span className="font-mono text-xs break-all">
                              {transaction.txHash.substring(0, 20)}...
                            </span>
                          ) : 'N/A'}
                        </div>
                      </div>

                      {parseMetadata(transaction.metadata).transferReference && (
                        <div className="mb-4 p-3 bg-gray-700 rounded">
                          <span className="font-medium text-sm">Bank Transfer Reference:</span>
                          <br />
                          <span className="font-mono">{parseMetadata(transaction.metadata).transferReference}</span>
                        </div>
                      )}
                    </div>

                    <div className="flex gap-2 ml-4">
                      <Dialog>
                        <DialogTrigger asChild>
                          <Button variant="outline" size="sm" onClick={() => setSelectedTransaction(transaction)}>
                            <Eye className="w-4 h-4 mr-1" />
                            Details
                          </Button>
                        </DialogTrigger>
                        <DialogContent className="bg-gray-800 border-gray-700 max-w-2xl">
                          <DialogHeader>
                            <DialogTitle className="text-white">Transaction Details</DialogTitle>
                          </DialogHeader>
                          <div className="space-y-4">
                            <pre className="bg-gray-900 p-4 rounded text-sm overflow-auto">
                              {JSON.stringify(transaction, null, 2)}
                            </pre>
                          </div>
                        </DialogContent>
                      </Dialog>

                      <Button
                        onClick={() => approveTransactionMutation.mutate({ id: transaction.id, action: 'approve' })}
                        disabled={approveTransactionMutation.isPending}
                        className="bg-green-600 hover:bg-green-700"
                        size="sm"
                      >
                        <CheckCircle className="w-4 h-4 mr-1" />
                        Approve
                      </Button>

                      <Dialog>
                        <DialogTrigger asChild>
                          <Button variant="destructive" size="sm">
                            <XCircle className="w-4 h-4 mr-1" />
                            Reject
                          </Button>
                        </DialogTrigger>
                        <DialogContent className="bg-gray-800 border-gray-700">
                          <DialogHeader>
                            <DialogTitle className="text-white">Reject Transaction</DialogTitle>
                          </DialogHeader>
                          <div className="space-y-4">
                            <div>
                              <label className="text-sm font-medium text-gray-300">Rejection Reason</label>
                              <Textarea
                                value={rejectionReason}
                                onChange={(e) => setRejectionReason(e.target.value)}
                                placeholder="Enter reason for rejection..."
                                className="bg-gray-700 border-gray-600 text-white mt-2"
                              />
                            </div>
                            <Button
                              onClick={() => approveTransactionMutation.mutate({ 
                                id: transaction.id, 
                                action: 'reject', 
                                reason: rejectionReason 
                              })}
                              disabled={approveTransactionMutation.isPending || !rejectionReason}
                              variant="destructive"
                              className="w-full"
                            >
                              {approveTransactionMutation.isPending ? 'Processing...' : 'Confirm Rejection'}
                            </Button>
                          </div>
                        </DialogContent>
                      </Dialog>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/MarketsPage.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { TrendingUp, TrendingDown, Search } from "lucide-react";
import type { MarketData } from "@shared/schema";

export default function MarketsPage() {
  const [searchQuery, setSearchQuery] = useState("");
  const [activeTab, setActiveTab] = useState("Spot");

  const { data: marketData, isLoading } = useQuery<MarketData[]>({
    queryKey: ["/api/market-data"],
  });

  const filteredMarketData = marketData?.filter(item =>
    item.symbol.toLowerCase().includes(searchQuery.toLowerCase())
  ) || [];

  const tabs = ["Favorites", "Options", "Spot"];

  return (
    <div className="min-h-screen bg-[#1a1b2e] pt-16">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">

        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-white mb-8">Markets</h1>

          {/* Tabs */}
          <div className="flex items-center space-x-2 mb-8">
            {tabs.map((tab) => (
              <Button
                key={tab}
                variant={activeTab === tab ? "default" : "outline"}
                size="sm"
                onClick={() => setActiveTab(tab)}
                className={activeTab === tab
                  ? "bg-gradient-to-r from-purple-600 to-blue-600 text-white border-0"
                  : "bg-transparent border-gray-600 text-gray-300 hover:bg-[#2a2d47]"
                }
              >
                {tab}
              </Button>
            ))}
          </div>

          {/* Search */}
          <div className="flex items-center space-x-4 mb-8">
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                type="text"
                placeholder="Search"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10 bg-[#2a2d47] border-gray-600 text-white placeholder-gray-400"
              />
            </div>
            <Button className="bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 text-white">
              Search
            </Button>
          </div>
        </div>

        {/* Market Data Table */}
        <Card className="bg-[#2a2d47] border-gray-600">
          <CardContent className="p-0">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b border-gray-600">
                    <th className="text-left p-6 font-semibold text-gray-300">Name</th>
                    <th className="text-right p-6 font-semibold text-gray-300">Price</th>
                    <th className="text-right p-6 font-semibold text-gray-300">24h Change</th>
                  </tr>
                </thead>
                <tbody>
                  {isLoading ? (
                    <tr>
                      <td colSpan={3} className="p-8 text-center text-gray-400">
                        Loading market data...
                      </td>
                    </tr>
                  ) : filteredMarketData?.length ? (
                    filteredMarketData.map((data, index) => (
                      <tr key={`${data.symbol}-${index}`} className="border-b border-gray-600 hover:bg-[#1a1b2e]/50 transition cursor-pointer">
                        <td className="p-6">
                          <div className="flex items-center space-x-4">
                            <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
                              <span className="text-sm font-bold text-white">
                                {data.symbol.replace('USDT', '').substring(0, 2)}
                              </span>
                            </div>
                            <div>
                              <div className="font-semibold text-white text-lg">{data.symbol.replace('USDT', '')}</div>
                              <div className="text-sm text-gray-400">
                                {data.symbol} / USDT
                              </div>
                            </div>
                          </div>
                        </td>
                        <td className="p-6 text-right">
                          <div className="font-mono text-white text-lg font-semibold">
                            ${parseFloat(data.price).toLocaleString('en-US', {
                              minimumFractionDigits: 2,
                              maximumFractionDigits: data.price.includes('.') ? data.price.split('.')[1].length : 2
                            })}
                          </div>
                        </td>
                        <td className="p-6 text-right">
                          <div className={`flex items-center justify-end font-semibold ${
                            parseFloat(data.priceChangePercent24h || '0') >= 0 ? 'text-green-400' : 'text-red-400'
                          }`}>
                            {parseFloat(data.priceChangePercent24h || '0') >= 0 ? (
                              <TrendingUp className="w-4 h-4 mr-1" />
                            ) : (
                              <TrendingDown className="w-4 h-4 mr-1" />
                            )}
                            {parseFloat(data.priceChangePercent24h || '0').toFixed(2)}%
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan={3} className="p-8 text-center text-gray-400">
                        {searchQuery ? 'No results found' : 'No market data available'}
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>

        {/* Pagination */}
        {filteredMarketData?.length > 0 && (
          <div className="flex justify-center mt-8">
            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                size="sm"
                className="w-8 h-8 rounded-full bg-[#2a2d47] border-gray-600 text-gray-300 hover:bg-[#1a1b2e]"
              >
                1
              </Button>
              <Button
                variant="outline"
                size="sm"
                className="w-8 h-8 rounded-full bg-transparent border-gray-600 text-gray-400 hover:bg-[#2a2d47]"
              >
                2
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/SignupPage.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";

export default function SignupPage() {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: ""
  });
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      toast({
        title: "Error",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      // Handle signup logic here
      console.log("Signup data:", formData);
      toast({
        title: "Success",
        description: "Account created successfully!",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create account",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#1a1b2e] pt-16 flex items-center justify-center">
      <div className="max-w-md w-full mx-auto px-4">
        <Card className="bg-[#2a2d47] border-gray-600">
          <CardContent className="p-8">
            <div className="text-center mb-8">
              <div className="flex items-center justify-center space-x-2 mb-4">
                <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-blue-500 rounded-lg flex items-center justify-center">
                  <span className="text-white font-bold text-sm">M</span>
                </div>
                <span className="text-white font-bold text-xl">METACHROME</span>
              </div>
              <h1 className="text-2xl font-bold text-white mb-2">Create Account</h1>
              <p className="text-gray-400">Join METACHROME and start trading</p>
            </div>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <Label htmlFor="username" className="text-gray-300">Username</Label>
                <Input
                  id="username"
                  name="username"
                  type="text"
                  value={formData.username}
                  onChange={handleInputChange}
                  placeholder="Enter your username"
                  className="mt-2 bg-[#1a1b2e] border-gray-600 text-white placeholder-gray-400"
                  required
                />
              </div>

              <div>
                <Label htmlFor="email" className="text-gray-300">Email</Label>
                <Input
                  id="email"
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Enter your email"
                  className="mt-2 bg-[#1a1b2e] border-gray-600 text-white placeholder-gray-400"
                  required
                />
              </div>

              <div>
                <Label htmlFor="password" className="text-gray-300">Password</Label>
                <Input
                  id="password"
                  name="password"
                  type="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter your password"
                  className="mt-2 bg-[#1a1b2e] border-gray-600 text-white placeholder-gray-400"
                  required
                />
              </div>

              <div>
                <Label htmlFor="confirmPassword" className="text-gray-300">Confirm Password</Label>
                <Input
                  id="confirmPassword"
                  name="confirmPassword"
                  type="password"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  placeholder="Confirm your password"
                  className="mt-2 bg-[#1a1b2e] border-gray-600 text-white placeholder-gray-400"
                  required
                />
              </div>

              <Button
                type="submit"
                disabled={isLoading}
                className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 text-white"
              >
                {isLoading ? "Creating Account..." : "Sign Up"}
              </Button>
            </form>

            <div className="mt-6 text-center">
              <p className="text-gray-400">
                Already have an account?{" "}
                <Link href="/login" className="text-purple-400 hover:text-purple-300">
                  Sign in
                </Link>
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

