import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { useToast } from '@/hooks/use-toast';
import { useWebSocket } from '@/hooks/useWebSocket';
import { useAuth } from '@/hooks/useAuth';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { ArrowUp, ArrowDown, TrendingUp, TrendingDown } from 'lucide-react';

interface MarketData {
  id: string;
  symbol: string;
  price: string;
  priceChange24h: string;
  priceChangePercent24h: string;
  high24h: string;
  low24h: string;
  volume24h: string;
  timestamp: string;
}

interface OptionsSettings {
  id: string;
  duration: number;
  minAmount: string;
  profitPercentage: string;
  isActive: boolean;
}

interface ActiveTrade {
  id: string;
  symbol: string;
  type: string;
  direction: string;
  amount: string;
  price: string;
  duration: number;
  expiresAt: string;
  status: string;
  profit?: string;
}

export default function TradingPage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { lastMessage } = useWebSocket();

  const [selectedSymbol, setSelectedSymbol] = useState('BTCUSDT');
  const [selectedDuration, setSelectedDuration] = useState<number>(30);
  const [tradeAmount, setTradeAmount] = useState('');
  const [countdown, setCountdown] = useState<number | null>(null);
  const [currentPrice, setCurrentPrice] = useState<string>('0');

  // Fetch market data
  const { data: marketData } = useQuery<MarketData[]>({
    queryKey: ['/api/market-data'],
    refetchInterval: 5000,
  });

  // Fetch options settings
  const { data: optionsSettings } = useQuery<OptionsSettings[]>({
    queryKey: ['/api/options-settings'],
  });

  // Fetch user's active trades
  const { data: activeTrades } = useQuery<ActiveTrade[]>({
    queryKey: ['/api/trades/active'],
    enabled: !!user,
  });

  // Fetch user balances
  const { data: balances } = useQuery({
    queryKey: ['/api/balances'],
    enabled: !!user,
  });

  // Create trade mutation
  const createTradeMutation = useMutation({
    mutationFn: async (tradeData: {
      symbol: string;
      type: 'options';
      direction: 'up' | 'down';
      amount: string;
      duration: number;
    }) => {
      return await apiRequest('POST', '/api/trades', tradeData);
    },
    onSuccess: () => {
      toast({
        title: 'Trade Placed Successfully',
        description: 'Your binary options trade has been placed.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/trades/active'] });
      queryClient.invalidateQueries({ queryKey: ['/api/balances'] });
      setTradeAmount('');
    },
    onError: (error) => {
      toast({
        title: 'Trade Failed',
        description: error.message || 'Failed to place trade.',
        variant: 'destructive',
      });
    },
  });

  // Update price from WebSocket
  useEffect(() => {
    if (lastMessage?.type === 'price_update' && lastMessage.data) {
      const data = lastMessage.data;
      if (data.symbol === selectedSymbol) {
        setCurrentPrice(data.price);
      }
    }
  }, [lastMessage, selectedSymbol]);

  // Set initial price from market data
  useEffect(() => {
    if (marketData) {
      const symbolData = marketData.find(d => d.symbol === selectedSymbol);
      if (symbolData) {
        setCurrentPrice(symbolData.price);
      }
    }
  }, [marketData, selectedSymbol]);

  // Countdown timer for active trades
  useEffect(() => {
    const interval = setInterval(() => {
      if (activeTrades) {
        const now = new Date().getTime();
        activeTrades.forEach(trade => {
          const expiresAt = new Date(trade.expiresAt).getTime();
          const timeLeft = Math.max(0, expiresAt - now);
          if (timeLeft === 0) {
            queryClient.invalidateQueries({ queryKey: ['/api/trades/active'] });
          }
        });
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [activeTrades, queryClient]);

  const selectedMarketData = marketData?.find(d => d.symbol === selectedSymbol);
  const availableSettings = optionsSettings?.filter(s => s.isActive) || [];
  const selectedSettings = availableSettings.find(s => s.duration === selectedDuration);
  const usdtBalance = Array.isArray(balances) ? balances.find((b: any) => b.symbol === 'USDT') : null;

  const handlePlaceTrade = (direction: 'up' | 'down') => {
    if (!user) {
      toast({
        title: 'Login Required',
        description: 'Please login to place trades.',
        variant: 'destructive',
      });
      return;
    }

    if (!tradeAmount || parseFloat(tradeAmount) < parseFloat(selectedSettings?.minAmount || '0')) {
      toast({
        title: 'Invalid Amount',
        description: `Minimum amount is ${selectedSettings?.minAmount} USDT`,
        variant: 'destructive',
      });
      return;
    }

    createTradeMutation.mutate({
      symbol: selectedSymbol,
      type: 'options',
      direction,
      amount: tradeAmount,
      duration: selectedDuration,
    });
  };

  const getTimeLeft = (expiresAt: string) => {
    const now = new Date().getTime();
    const expires = new Date(expiresAt).getTime();
    const timeLeft = Math.max(0, expires - now);
    const seconds = Math.floor(timeLeft / 1000);
    return seconds;
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 p-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">
            METACHROME Binary Options Trading
          </h1>
          <p className="text-gray-300">
            Trade binary options with customizable durations and admin-controlled outcomes
          </p>
        </div>

        <div className="grid lg:grid-cols-3 gap-6">
          {/* Trading Panel */}
          <div className="lg:col-span-2 space-y-6">
            {/* Market Selection */}
            <Card className="bg-slate-800/90 border-purple-500/20">
              <CardHeader>
                <CardTitle className="text-white flex items-center gap-2">
                  <TrendingUp className="w-5 h-5 text-purple-400" />
                  Market Selection
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
                  {marketData?.map((market) => (
                    <Button
                      key={market.symbol}
                      variant={selectedSymbol === market.symbol ? "default" : "outline"}
                      className={`p-4 h-auto flex flex-col items-start ${
                        selectedSymbol === market.symbol 
                          ? 'bg-purple-600 hover:bg-purple-700' 
                          : 'bg-slate-700/50 hover:bg-slate-700'
                      }`}
                      onClick={() => setSelectedSymbol(market.symbol)}
                    >
                      <div className="font-semibold text-white">
                        {market.symbol.replace('USDT', '/USDT')}
                      </div>
                      <div className="text-sm text-gray-300">
                        ${parseFloat(market.price).toLocaleString()}
                      </div>
                      <div className={`text-xs flex items-center gap-1 ${
                        parseFloat(market.priceChangePercent24h) >= 0 
                          ? 'text-green-400' 
                          : 'text-red-400'
                      }`}>
                        {parseFloat(market.priceChangePercent24h) >= 0 ? (
                          <ArrowUp className="w-3 h-3" />
                        ) : (
                          <ArrowDown className="w-3 h-3" />
                        )}
                        {parseFloat(market.priceChangePercent24h).toFixed(2)}%
                      </div>
                    </Button>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Current Price Display */}
            <Card className="bg-slate-800/90 border-purple-500/20">
              <CardContent className="p-6">
                <div className="text-center">
                  <div className="text-2xl font-bold text-white mb-2">
                    {selectedSymbol.replace('USDT', '/USDT')}
                  </div>
                  <div className="text-4xl font-bold text-purple-400 mb-4">
                    ${parseFloat(currentPrice).toLocaleString()}
                  </div>
                  {selectedMarketData && (
                    <div className={`flex items-center justify-center gap-2 ${
                      parseFloat(selectedMarketData.priceChangePercent24h) >= 0 
                        ? 'text-green-400' 
                        : 'text-red-400'
                    }`}>
                      {parseFloat(selectedMarketData.priceChangePercent24h) >= 0 ? (
                        <TrendingUp className="w-5 h-5" />
                      ) : (
                        <TrendingDown className="w-5 h-5" />
                      )}
                      <span className="font-semibold">
                        {parseFloat(selectedMarketData.priceChangePercent24h).toFixed(2)}% 
                        (${parseFloat(selectedMarketData.priceChange24h).toFixed(2)})
                      </span>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Trading Form */}
            <Card className="bg-slate-800/90 border-purple-500/20">
              <CardHeader>
                <CardTitle className="text-white">Place Binary Options Trade</CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Duration
                    </label>
                    <Select value={selectedDuration.toString()} onValueChange={(value) => setSelectedDuration(parseInt(value))}>
                      <SelectTrigger className="bg-slate-700 border-slate-600 text-white">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent className="bg-slate-700 border-slate-600">
                        {availableSettings.map((setting) => (
                          <SelectItem key={setting.id} value={setting.duration.toString()}>
                            {setting.duration} seconds - Min: ${setting.minAmount} - Profit: {setting.profitPercentage}%
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Amount (USDT)
                    </label>
                    <Input
                      type="number"
                      value={tradeAmount}
                      onChange={(e) => setTradeAmount(e.target.value)}
                      placeholder="Enter amount"
                      className="bg-slate-700 border-slate-600 text-white"
                      min={selectedSettings?.minAmount || "0"}
                      step="0.01"
                    />
                    <div className="text-xs text-gray-400 mt-1">
                      Min: ${selectedSettings?.minAmount || '0'} | 
                      Available: ${usdtBalance?.available || '0'}
                    </div>
                  </div>
                </div>

                {selectedSettings && (
                  <div className="bg-slate-700/50 p-4 rounded-lg">
                    <div className="grid grid-cols-2 gap-4 text-sm text-gray-300">
                      <div>
                        <span className="font-medium">Profit Percentage:</span> {selectedSettings.profitPercentage}%
                      </div>
                      <div>
                        <span className="font-medium">Potential Profit:</span> ${
                          tradeAmount 
                            ? (parseFloat(tradeAmount) * parseFloat(selectedSettings.profitPercentage) / 100).toFixed(2)
                            : '0.00'
                        }
                      </div>
                    </div>
                  </div>
                )}

                <div className="grid grid-cols-2 gap-4">
                  <Button
                    onClick={() => handlePlaceTrade('up')}
                    disabled={createTradeMutation.isPending || !user}
                    className="bg-green-600 hover:bg-green-700 text-white h-12 text-lg font-semibold"
                  >
                    <ArrowUp className="w-5 h-5 mr-2" />
                    UP / CALL
                  </Button>
                  <Button
                    onClick={() => handlePlaceTrade('down')}
                    disabled={createTradeMutation.isPending || !user}
                    className="bg-red-600 hover:bg-red-700 text-white h-12 text-lg font-semibold"
                  >
                    <ArrowDown className="w-5 h-5 mr-2" />
                    DOWN / PUT
                  </Button>
                </div>

                {!user && (
                  <div className="text-center text-yellow-400">
                    Please login to place trades
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Active Trades Sidebar */}
          <div className="space-y-6">
            <Card className="bg-slate-800/90 border-purple-500/20">
              <CardHeader>
                <CardTitle className="text-white">Active Trades</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {activeTrades && activeTrades.length > 0 ? (
                  activeTrades.map((trade) => {
                    const timeLeft = getTimeLeft(trade.expiresAt);
                    const progress = ((trade.duration - timeLeft) / trade.duration) * 100;

                    return (
                      <div key={trade.id} className="bg-slate-700/50 p-4 rounded-lg">
                        <div className="flex justify-between items-start mb-2">
                          <div>
                            <div className="font-semibold text-white">
                              {trade.symbol.replace('USDT', '/USDT')}
                            </div>
                            <div className="text-sm text-gray-300">
                              ${trade.amount} USDT
                            </div>
                          </div>
                          <Badge 
                            variant={trade.direction === 'up' ? 'default' : 'destructive'}
                            className={trade.direction === 'up' ? 'bg-green-600' : 'bg-red-600'}
                          >
                            {trade.direction.toUpperCase()}
                          </Badge>
                        </div>

                        <div className="space-y-2">
                          <div className="flex justify-between text-sm">
                            <span className="text-gray-300">Time Left:</span>
                            <span className="text-white font-mono">
                              {formatTime(timeLeft)}
                            </span>
                          </div>
                          <Progress value={progress} className="h-2" />
                          <div className="flex justify-between text-sm">
                            <span className="text-gray-300">Entry Price:</span>
                            <span className="text-white">${parseFloat(trade.price).toFixed(2)}</span>
                          </div>
                        </div>
                      </div>
                    );
                  })
                ) : (
                  <div className="text-gray-400 text-center py-8">
                    No active trades
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Balance Card */}
            {user && (
              <Card className="bg-slate-800/90 border-purple-500/20">
                <CardHeader>
                  <CardTitle className="text-white">Account Balance</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold text-purple-400">
                    ${usdtBalance?.available || '0.00'} USDT
                  </div>
                  <div className="text-sm text-gray-300 mt-1">
                    Locked: ${usdtBalance?.locked || '0.00'} USDT
                  </div>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/TransactionHistory.tsx.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { useAuth } from '@/hooks/useAuth';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { 
  History, 
  Download, 
  Upload, 
  RefreshCw, 
  Search, 
  Filter,
  Calendar,
  ArrowUpRight,
  ArrowDownLeft,
  Clock,
  CheckCircle,
  XCircle,
  AlertCircle
} from 'lucide-react';

export default function TransactionHistory() {
  const { user } = useAuth();
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [dateRange, setDateRange] = useState('all');

  // Fetch user transactions
  const { data: transactions, isLoading } = useQuery({
    queryKey: [`/api/users/${user?.id}/transactions`],
    enabled: !!user?.id,
  });

  // Fetch user trades for trading history
  const { data: trades } = useQuery({
    queryKey: [`/api/users/${user?.id}/trades`],
    enabled: !!user?.id,
  });

  // Filter transactions
  const filteredTransactions = transactions?.filter((transaction: any) => {
    const matchesSearch = transaction.currency?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         transaction.type?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         transaction.txHash?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesType = filterType === 'all' || transaction.type === filterType;
    const matchesStatus = filterStatus === 'all' || transaction.status === filterStatus;

    return matchesSearch && matchesType && matchesStatus;
  }) || [];

  const getTransactionIcon = (type: string) => {
    switch (type) {
      case 'deposit':
        return <Download className="h-4 w-4" />;
      case 'withdraw':
        return <Upload className="h-4 w-4" />;
      case 'transfer':
        return <RefreshCw className="h-4 w-4" />;
      default:
        return <RefreshCw className="h-4 w-4" />;
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'pending':
        return <Clock className="h-4 w-4 text-yellow-500" />;
      case 'failed':
        return <XCircle className="h-4 w-4 text-red-500" />;
      default:
        return <AlertCircle className="h-4 w-4 text-gray-500" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-600';
      case 'pending':
        return 'bg-yellow-600';
      case 'failed':
        return 'bg-red-600';
      default:
        return 'bg-gray-600';
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'deposit':
        return 'text-green-400';
      case 'withdraw':
        return 'text-red-400';
      case 'transfer':
        return 'text-blue-400';
      default:
        return 'text-gray-400';
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 pt-20 pb-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-white mb-2 flex items-center gap-3">
            <History className="h-8 w-8" />
            Transaction History
          </h1>
          <p className="text-gray-400">
            View and manage all your transactions, deposits, and withdrawals
          </p>
        </div>

        {/* Filters */}
        <Card className="bg-gray-800 border-gray-700 mb-8">
          <CardHeader>
            <CardTitle className="text-white flex items-center gap-2">
              <Filter className="h-5 w-5" />
              Filters
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div>
                <label className="text-sm text-gray-400 mb-2 block">Search</label>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <Input
                    placeholder="Search transactions..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 bg-gray-700 border-gray-600 text-white"
                  />
                </div>
              </div>

              <div>
                <label className="text-sm text-gray-400 mb-2 block">Type</label>
                <Select value={filterType} onValueChange={setFilterType}>
                  <SelectTrigger className="bg-gray-700 border-gray-600 text-white">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="bg-gray-700 border-gray-600">
                    <SelectItem value="all">All Types</SelectItem>
                    <SelectItem value="deposit">Deposits</SelectItem>
                    <SelectItem value="withdraw">Withdrawals</SelectItem>
                    <SelectItem value="transfer">Transfers</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm text-gray-400 mb-2 block">Status</label>
                <Select value={filterStatus} onValueChange={setFilterStatus}>
                  <SelectTrigger className="bg-gray-700 border-gray-600 text-white">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="bg-gray-700 border-gray-600">
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="completed">Completed</SelectItem>
                    <SelectItem value="pending">Pending</SelectItem>
                    <SelectItem value="failed">Failed</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm text-gray-400 mb-2 block">Date Range</label>
                <Select value={dateRange} onValueChange={setDateRange}>
                  <SelectTrigger className="bg-gray-700 border-gray-600 text-white">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="bg-gray-700 border-gray-600">
                    <SelectItem value="all">All Time</SelectItem>
                    <SelectItem value="today">Today</SelectItem>
                    <SelectItem value="week">This Week</SelectItem>
                    <SelectItem value="month">This Month</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Transaction List */}
        <Card className="bg-gray-800 border-gray-700">
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="text-white">Transactions</CardTitle>
                <CardDescription className="text-gray-400">
                  {filteredTransactions.length} transactions found
                </CardDescription>
              </div>
              <Button variant="outline" className="border-gray-600 text-gray-300 hover:bg-gray-700">
                <Download className="h-4 w-4 mr-2" />
                Export
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="flex items-center justify-center py-12">
                <RefreshCw className="h-8 w-8 animate-spin text-gray-400" />
              </div>
            ) : filteredTransactions.length > 0 ? (
              <div className="space-y-4">
                {filteredTransactions.map((transaction: any) => (
                  <div key={transaction.id} className="flex items-center justify-between p-4 bg-gray-700/50 rounded-lg hover:bg-gray-700/70 transition-colors">
                    <div className="flex items-center gap-4">
                      <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                        transaction.type === 'deposit' ? 'bg-green-600/20 text-green-400' :
                        transaction.type === 'withdraw' ? 'bg-red-600/20 text-red-400' :
                        'bg-blue-600/20 text-blue-400'
                      }`}>
                        {getTransactionIcon(transaction.type)}
                      </div>
                      <div>
                        <div className="flex items-center gap-2">
                          <span className="font-medium text-white capitalize">{transaction.type}</span>
                          <Badge className={getStatusColor(transaction.status)}>
                            {transaction.status}
                          </Badge>
                        </div>
                        <div className="text-sm text-gray-400 flex items-center gap-2">
                          <Clock className="h-3 w-3" />
                          {new Date(transaction.createdAt).toLocaleString()}
                        </div>
                        {transaction.txHash && (
                          <div className="text-xs text-gray-500 font-mono">
                            TX: {transaction.txHash.slice(0, 10)}...{transaction.txHash.slice(-6)}
                          </div>
                        )}
                      </div>
                    </div>

                    <div className="text-right">
                      <div className={`font-medium text-lg ${getTypeColor(transaction.type)}`}>
                        {transaction.type === 'deposit' ? '+' : transaction.type === 'withdraw' ? '-' : ''}
                        ${parseFloat(transaction.amount).toFixed(2)}
                      </div>
                      <div className="text-sm text-gray-400">
                        {transaction.currency || 'USDT'}
                      </div>
                      {transaction.fee && parseFloat(transaction.fee) > 0 && (
                        <div className="text-xs text-gray-500">
                          Fee: ${parseFloat(transaction.fee).toFixed(2)}
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <History className="h-16 w-16 text-gray-500 mx-auto mb-4" />
                <h3 className="text-xl font-medium text-white mb-2">No transactions found</h3>
                <p className="text-gray-400 mb-6">
                  {searchTerm || filterType !== 'all' || filterStatus !== 'all' 
                    ? 'Try adjusting your filters to see more results'
                    : 'Your transaction history will appear here once you start trading'
                  }
                </p>
                <Button 
                  onClick={() => {
                    setSearchTerm('');
                    setFilterType('all');
                    setFilterStatus('all');
                    setDateRange('all');
                  }}
                  variant="outline" 
                  className="border-gray-600 text-gray-300 hover:bg-gray-700"
                >
                  Clear Filters
                </Button>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

