import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { web3Service } from "@/services/web3Service";
import StripePayment from "@/components/StripePayment";
import { CreditCard, ArrowUpRight, ArrowDownLeft, Send, Download, Users, Wallet } from "lucide-react";

export default function WalletPage() {
  const [activeTab, setActiveTab] = useState("Balance");
  const [walletConnected, setWalletConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState<string>('');
  const [depositAmount, setDepositAmount] = useState('');
  const [withdrawAddress, setWithdrawAddress] = useState('');
  const [withdrawAmount, setWithdrawAmount] = useState('');
  const [selectedCrypto, setSelectedCrypto] = useState('USDT');

  // Modal states for deposit confirmations
  const [showTxHashModal, setShowTxHashModal] = useState(false);
  const [showBankRefModal, setShowBankRefModal] = useState(false);
  const [showStripeModal, setShowStripeModal] = useState(false);
  const [txHash, setTxHash] = useState('');
  const [bankRef, setBankRef] = useState('');
  const [pendingDepositData, setPendingDepositData] = useState<any>(null);

  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  // Fetch real user balances
  const { data: userBalances, isLoading: balancesLoading } = useQuery({
    queryKey: ["/api/balances"],
    enabled: !!user,
  });

  // Fetch real market data for price calculations
  const { data: marketData } = useQuery({
    queryKey: ['/api/market-data'],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  const tabs = [
    { id: "Balance", label: "Balance" },
    { id: "Deposit", label: "Deposit" },
    { id: "Withdraw", label: "Withdraw" },
    { id: "Transfer", label: "Transfer" },
    { id: "Convert", label: "Convert" }
  ];

  // Helper function to get market price
  const getMarketPrice = (symbol: string): number => {
    if (symbol === 'USDT') return 1;
    const marketItem = marketData?.find(item => item.symbol === `${symbol}USDT`);
    return marketItem ? parseFloat(marketItem.price) : 0;
  };

  // Calculate total balance in USDT
  const totalBalanceUSDT = userBalances?.reduce((sum: number, balance: any) => {
    const price = getMarketPrice(balance.symbol);
    return sum + parseFloat(balance.available || '0') * price;
  }, 0) || 0;

  // Initialize Web3 on component mount
  useEffect(() => {
    const initWeb3 = async () => {
      const initialized = await web3Service.initialize();
      if (initialized) {
        const account = await web3Service.getCurrentAccount();
        if (account) {
          setWalletConnected(true);
          setWalletAddress(account);
        }
      }
    };
    initWeb3();
  }, []);

  // Connect wallet mutation
  const connectWalletMutation = useMutation({
    mutationFn: async () => {
      const accounts = await web3Service.connectWallet();
      return accounts[0];
    },
    onSuccess: (account) => {
      setWalletConnected(true);
      setWalletAddress(account);
      toast({
        title: 'Wallet Connected',
        description: `Connected to ${account.slice(0, 6)}...${account.slice(-4)}`,
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Connection Failed',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  // Production-ready deposit mutation
  const depositMutation = useMutation({
    mutationFn: async (data: { amount: string; currency: string; method: string; txHash?: string; paymentData?: any }) => {
      const response = await fetch('/api/transactions/deposit', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          amount: data.amount,
          currency: data.currency,
          method: data.method,
          txHash: data.txHash,
          paymentData: data.paymentData
        }),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Deposit failed');
      }
      return response.json();
    },
    onSuccess: (data) => {
      const isCompleted = data.transaction.status === 'completed';
      toast({
        title: isCompleted ? 'Deposit Successful' : 'Deposit Submitted',
        description: isCompleted
          ? `Successfully deposited ${data.amount} ${data.currency}`
          : 'Your deposit has been submitted for verification. You will be notified once approved.',
        variant: isCompleted ? 'default' : 'default',
      });
      setDepositAmount('');
      if (isCompleted) {
        queryClient.invalidateQueries({ queryKey: ['/api/balances'] });
      }
    },
    onError: (error: any) => {
      toast({
        title: 'Deposit Failed',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  // Withdraw mutation
  const withdrawMutation = useMutation({
    mutationFn: async (data: { amount: string; currency: string; address: string }) => {
      const response = await fetch('/api/transactions/withdraw', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          amount: data.amount,
          currency: data.currency,
          address: data.address,
          method: 'crypto'
        }),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Withdrawal failed');
      }
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: 'Withdrawal Initiated',
        description: `Withdrawal of ${data.amount} ${data.currency} has been initiated`,
      });
      setWithdrawAddress('');
      setWithdrawAmount('');
      queryClient.invalidateQueries({ queryKey: ['/api/balances'] });
    },
    onError: (error: any) => {
      toast({
        title: 'Withdrawal Failed',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  // Handle transaction hash submission
  const handleTxHashSubmit = () => {
    if (!txHash.trim()) {
      toast({
        title: 'Transaction Hash Required',
        description: 'Please provide a valid transaction hash for verification',
        variant: 'destructive',
      });
      return;
    }

    depositMutation.mutate({
      ...pendingDepositData,
      txHash: txHash
    });

    setShowTxHashModal(false);
    setTxHash('');
    setPendingDepositData(null);
  };

  // Handle bank reference submission
  const handleBankRefSubmit = () => {
    if (!bankRef.trim()) {
      toast({
        title: 'Transfer Reference Required',
        description: 'Please provide your bank transfer reference number',
        variant: 'destructive',
      });
      return;
    }

    depositMutation.mutate({
      ...pendingDepositData,
      paymentData: { transferReference: bankRef }
    });

    setShowBankRefModal(false);
    setBankRef('');
    setPendingDepositData(null);
  };

  // Handle Stripe payment success
  const handleStripeSuccess = (paymentIntentId: string) => {
    depositMutation.mutate({
      ...pendingDepositData,
      paymentData: { paymentIntentId }
    });

    setShowStripeModal(false);
    setPendingDepositData(null);
  };

  // Handle Stripe payment error
  const handleStripeError = (error: string) => {
    toast({
      title: 'Payment Failed',
      description: error,
      variant: 'destructive',
    });
  };

  // Use real balances from API
  const balances = userBalances || [];

  const cryptoNames: Record<string, string> = {
    BTC: "Bitcoin",
    ETH: "Ethereum",
    USDT: "Tether",
  };



  const depositMethods = [
    {
      icon: <CreditCard className="w-8 h-8" />,
      title: "Crypto Deposit",
      description: "Deposit cryptocurrency directly to your wallet address",
      action: "Deposit",
      method: "crypto"
    },
    {
      icon: <CreditCard className="w-8 h-8" />,
      title: "Credit/Debit Card",
      description: "Buy crypto instantly with your credit or debit card",
      action: "Card",
      method: "card"
    },
    {
      icon: <ArrowDownLeft className="w-8 h-8" />,
      title: "Bank Transfer",
      description: "Transfer funds directly from your bank account",
      action: "Bank",
      method: "bank"
    }
  ];

  return (
    <div className="min-h-screen bg-[#1a1b2e] pt-16">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">

        {/* Header Tabs */}
        <div className="mb-8">
          <div className="flex items-center space-x-1 mb-8 border-b border-gray-600">
            {tabs.map((tab) => (
              <Button
                key={tab.id}
                variant="ghost"
                size="sm"
                onClick={() => setActiveTab(tab.id)}
                className={activeTab === tab.id
                  ? "bg-transparent text-white border-b-2 border-purple-500 rounded-none pb-3"
                  : "bg-transparent text-gray-400 hover:text-white rounded-none pb-3"
                }
              >
                {tab.label}
              </Button>
            ))}
          </div>
        </div>

        {activeTab === "Balance" && (
          <div className="space-y-8">
            {/* Balance Header */}
            <div>
              <h1 className="text-4xl font-bold text-white mb-2">Balance</h1>

              {/* Wallet Connection Status */}
              {!walletConnected && (
                <div className="mb-6 p-4 bg-yellow-900/20 border border-yellow-600/30 rounded-lg">
                  <div className="flex items-center justify-between">
                    <div>
                      <h4 className="text-yellow-400 font-semibold">Connect Your Wallet</h4>
                      <p className="text-yellow-300/80 text-sm">Connect MetaMask to enable deposits and withdrawals</p>
                    </div>
                    <Button
                      onClick={() => connectWalletMutation.mutate()}
                      disabled={connectWalletMutation.isPending}
                      className="bg-yellow-600 hover:bg-yellow-700"
                    >
                      <Wallet className="w-4 h-4 mr-2" />
                      {connectWalletMutation.isPending ? 'Connecting...' : 'Connect'}
                    </Button>
                  </div>
                </div>
              )}

              {/* Total Balance */}
              <div className="mb-8">
                <div className="flex items-center space-x-2 mb-2">
                  <span className="text-gray-400 text-sm">Total Balances</span>
                  <span className="text-gray-400">üí∞</span>
                  {walletConnected && (
                    <Badge variant="outline" className="text-green-400 border-green-400">
                      Wallet Connected: {walletAddress.slice(0, 6)}...{walletAddress.slice(-4)}
                    </Badge>
                  )}
                </div>
                <div className="text-4xl font-bold text-white">
                  {balancesLoading ? (
                    <span className="animate-pulse">Loading...</span>
                  ) : (
                    `${totalBalanceUSDT.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })} USDT`
                  )}
                </div>
              </div>

              {/* Account Deposit Section */}
              <Card className="bg-[#2a2d47] border-gray-600 mb-8">
                <CardContent className="p-6">
                  <h3 className="text-white font-semibold mb-4">Account deposit</h3>
                  <p className="text-gray-400 text-sm mb-6">
                    Fast payment deposit methods are supported, and you can choose the method that suits you best.
                  </p>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {depositMethods.map((method, index) => (
                      <div key={index} className="flex items-start space-x-4 p-4 border border-gray-600 rounded-lg bg-[#1a1b2e]/50">
                        <div className="flex-shrink-0 text-purple-400">
                          {method.icon}
                        </div>
                        <div className="flex-1">
                          <h4 className="text-white font-semibold mb-2">{method.title}</h4>
                          <p className="text-gray-400 text-sm mb-4">{method.description}</p>
                          <Dialog>
                            <DialogTrigger asChild>
                              <Button
                                size="sm"
                                className="bg-gray-600 hover:bg-gray-500 text-white border border-gray-500"
                              >
                                {method.action}
                              </Button>
                            </DialogTrigger>
                            <DialogContent className="bg-gray-800 border-gray-700 max-w-md">
                              <DialogHeader>
                                <DialogTitle className="text-white">{method.title}</DialogTitle>
                              </DialogHeader>
                              <div className="space-y-4">
                                <div>
                                  <Label htmlFor="amount" className="text-gray-300">Amount</Label>
                                  <Input
                                    id="amount"
                                    type="number"
                                    placeholder="Enter amount"
                                    value={depositAmount}
                                    onChange={(e) => setDepositAmount(e.target.value)}
                                    className="bg-gray-700 border-gray-600 text-white"
                                  />
                                </div>
                                <div>
                                  <Label htmlFor="currency" className="text-gray-300">Currency</Label>
                                  <div className="grid grid-cols-3 gap-2 mt-2">
                                    {['USDT', 'BTC', 'ETH'].map((currency) => (
                                      <Button
                                        key={currency}
                                        variant={selectedCrypto === currency ? "default" : "outline"}
                                        size="sm"
                                        onClick={() => setSelectedCrypto(currency)}
                                        className={selectedCrypto === currency ?
                                          "bg-purple-600 hover:bg-purple-700" :
                                          "border-gray-600 text-gray-300 hover:bg-gray-700"
                                        }
                                      >
                                        {currency}
                                      </Button>
                                    ))}
                                  </div>
                                </div>
                                {method.method === 'crypto' && (
                                  <div className="p-4 bg-gray-700/50 rounded-lg">
                                    <p className="text-sm text-gray-400 mb-2">Deposit Address:</p>
                                    <p className="text-xs font-mono text-white bg-gray-800 p-2 rounded break-all">
                                      {walletAddress || '0x1234567890abcdef1234567890abcdef12345678'}
                                    </p>
                                    <p className="text-xs text-yellow-400 mt-2">
                                      ‚ö†Ô∏è Only send {selectedCrypto} to this address
                                    </p>
                                  </div>
                                )}
                                <Button
                                  onClick={() => {
                                    if (!depositAmount || parseFloat(depositAmount) <= 0) {
                                      toast({
                                        title: 'Invalid Amount',
                                        description: 'Please enter a valid amount',
                                        variant: 'destructive',
                                      });
                                      return;
                                    }

                                    // Handle different payment methods
                                    if (method.method === 'crypto') {
                                      // For crypto, show transaction hash modal
                                      setPendingDepositData({
                                        amount: depositAmount,
                                        currency: selectedCrypto,
                                        method: method.method
                                      });
                                      setShowTxHashModal(true);
                                    } else if (method.method === 'card') {
                                      // For card payments, show Stripe modal
                                      setPendingDepositData({
                                        amount: depositAmount,
                                        currency: selectedCrypto,
                                        method: method.method
                                      });
                                      setShowStripeModal(true);
                                    } else if (method.method === 'bank') {
                                      // For bank transfers, show reference modal
                                      setPendingDepositData({
                                        amount: depositAmount,
                                        currency: selectedCrypto,
                                        method: method.method
                                      });
                                      setShowBankRefModal(true);
                                    }
                                  }}
                                  disabled={depositMutation.isPending || !depositAmount}
                                  className="w-full bg-green-600 hover:bg-green-700"
                                >
                                  {depositMutation.isPending ? 'Processing...' : `Confirm ${method.action}`}
                                </Button>
                              </div>
                            </DialogContent>
                          </Dialog>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* My Assets */}
              <Card className="bg-[#2a2d47] border-gray-600">
                <CardContent className="p-6">
                  <h3 className="text-white font-semibold mb-6">My assets</h3>

                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="border-b border-gray-600">
                          <th className="text-left p-4 font-semibold text-gray-300">Name</th>
                          <th className="text-right p-4 font-semibold text-gray-300">Available amount</th>
                          <th className="text-right p-4 font-semibold text-gray-300">On order</th>
                          <th className="text-right p-4 font-semibold text-gray-300">Amount in USDT</th>
                        </tr>
                      </thead>
                      <tbody>
                        {balances.map((balance, index) => (
                          <tr key={index} className="border-b border-gray-600 hover:bg-[#1a1b2e]/50 transition">
                            <td className="p-4">
                              <div className="flex items-center space-x-3">
                                <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
                                  <span className="text-sm font-bold text-white">
                                    {balance.symbol.substring(0, 2)}
                                  </span>
                                </div>
                                <div>
                                  <div className="font-semibold text-white">{balance.symbol}</div>
                                  <div className="text-sm text-gray-400">{cryptoNames[balance.symbol] || balance.symbol}</div>
                                </div>
                              </div>
                            </td>
                            <td className="p-4 text-right font-mono text-white">
                              {parseFloat(balance.available || '0').toFixed(8)}
                            </td>
                            <td className="p-4 text-right font-mono text-gray-400">
                              {parseFloat(balance.locked || '0').toFixed(8)}
                            </td>
                            <td className="p-4 text-right font-mono text-white">
                              ${((parseFloat(balance.available || '0') + parseFloat(balance.locked || '0')) * getMarketPrice(balance.symbol)).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        )}

        {activeTab === "Deposit" && (
          <div className="space-y-8">
            <div>
              <h1 className="text-4xl font-bold text-white mb-8">Deposit Funds</h1>

              {/* Deposit Methods */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                {depositMethods.map((method, index) => (
                  <Card key={index} className="bg-[#2a2d47] border-gray-600 hover:border-purple-500/50 transition-colors cursor-pointer">
                    <CardContent className="p-6">
                      <div className="text-center">
                        <div className="w-16 h-16 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full mx-auto mb-4 flex items-center justify-center text-white">
                          {method.icon}
                        </div>
                        <h3 className="text-xl font-semibold text-white mb-2">{method.title}</h3>
                        <p className="text-gray-400 mb-4 text-sm">{method.description}</p>
                        <Dialog>
                          <DialogTrigger asChild>
                            <Button className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700">
                              {method.action}
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="bg-gray-800 border-gray-700 max-w-md">
                            <DialogHeader>
                              <DialogTitle className="text-white">{method.title}</DialogTitle>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <Label htmlFor="amount" className="text-gray-300">Amount</Label>
